import React, {useState} from "react";
import axios from "axios";
import {getCookie} from "../../helper/functions";
import "./style.css"
import md5 from "md5";
import {useDispatch, useSelector} from "react-redux";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faArrowCircleLeft} from "@fortawesome/free-solid-svg-icons";

const ChangePassword = () => {
    const [password, setPassword] = useState('');
    const [newPass, confirmPassword] = useState('');
    const [email, getEmail] = useState('');
    const [firstName, getFirstName] = useState('');
    const [lastName, getLastName] = useState('');
    const dispatch = useDispatch();

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            handleSubmit(event);
        }

    };


    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post("http://77.104.116.187:8000/v2/accounts/" + getCookie("accID") + "/users/" + getCookie("owID"),
            {
                data: {
                    "username": email,
                    "first_name": firstName,
                    "last_name": lastName,
                    "password": password,
                    "confirm_password": newPass

                }
            }, {
                headers: {
                    "X-Auth-Token": getCookie("monster_token")
                }
            }
        )
            .then(response => {
                console.log("yes")
                setChangePassStatus(false)
            })
            .catch(error => {
                console.log(error);
            });
    };
    const handleChangePass = (event) => {
        // console.log("passValue", event.target.value)
        setPassword(event.target.value)

    };
    const handleConfirmPass = (event) => {
        // console.log("passValue", event.target.value)
        confirmPassword(event.target.value)

    };
    const handleEmail = (event) => {
        // console.log("passValue", event.target.value)
        getEmail(event.target.value)
    };
    const handleFirstName = (event) => {
        // console.log("passValue", event.target.value)
        getFirstName(event.target.value)
    };
    const handleLastName = (event) => {
        // console.log("passValue", event.target.value)
        getLastName(event.target.value)
    };

    const setChangePassStatus = (showChangePasswordPage) => {
        dispatch({showChangePasswordPage, type: 'SHOW_CHANGE_PASS_PAGE'})
    }
    const handleChangePassPage = () => {
        setChangePassStatus(true);
    };
    const goBack = () => {
        setChangePassStatus(false)
    }
    return (
        <React.Fragment>
            <div>
                <FontAwesomeIcon color="white" height={"50px"} onClick={goBack} icon={faArrowCircleLeft}/>
            </div>
            <div className="row">
                <div className="col-sm-9 col-md-7 col-lg-5 mx-auto">
                    <div className="card card-change my-5">
                        <div className="card-body">
                            <h5 className="card-title text-center">Change Your Password</h5>

                            <form className="form-change">
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputEmail"
                                        placeholder="Email"
                                        onChange={handleEmail}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputEmail">Email</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputFirstName"
                                        placeholder="First name"
                                        onChange={handleFirstName}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputFirstName">First name</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputLastName"
                                        placeholder="Last name"
                                        onChange={handleLastName}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputLastName">Last name</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputNewPass"
                                        placeholder="New password"
                                        onChange={handleChangePass}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputNewPass">Password</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputConfirm"
                                        placeholder="Confirm password"
                                        onChange={handleConfirmPass}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputConfirm">Confirm Password</label>

                                </div>
                                <button
                                    type="submit"
                                    className="btn btn-lg btn-primary btn-block text-uppercase"
                                    id="changePass"
                                    onClick={handleSubmit}
                                >Change Password
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    );

}


export default ChangePassword;
