import React, {useState} from "react";
import axios from "axios";
import {getCookie} from "../../helper/functions";
import "./style.css"
import md5 from "md5";
import {useDispatch, useSelector} from "react-redux";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faArrowCircleLeft} from "@fortawesome/free-solid-svg-icons";

const ChangePassword = () => {
    const userData = useSelector(state => state.userData)
    const successMessage = useSelector(state => state.successMessage)
    const [password, setPassword] = useState('');
    const [newPass, confirmPassword] = useState('');
    const [currentPass, setCurrentPass] = useState('');
    const dispatch = useDispatch();

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            handleSubmit(event);
        }

    };


    const handleSubmit = (event) => {
        event.preventDefault();
        if (currentPass === undefined || currentPass === "") {
            document.getElementById("error-message").textContent = "please enter your current password"

        } else if (newPass === undefined || newPass === "") {
            document.getElementById("error-message").textContent = "please enter new password"
        } else if (password === undefined || password === "")
            document.getElementById("error-message").textContent = "please enter new password"

        else {
            // changeLoginButton("Sign in")
            axios.post("http://77.104.116.187:8000/v2/accounts/" + getCookie("accID") + "/users/" + getCookie("owID"),
                {
                    data: {
                        "username": userData[0].data.email,
                        "first_name": userData[0].data.first_name,
                        "last_name": userData[0].data.last_name,
                        "password": password,
                        "confirm_password": newPass
                    }
                }, {
                    headers: {
                        "X-Auth-Token": getCookie("monster_token")
                    }
                }
            )
                .then(response => {
                    console.log("yes")
                    setSuccessMessage("رمز عبور شما با موفقیت تغییر یافت. لطفا مجدد وارد شوید.")
                    // setChangePassStatus(false)
                })
                .catch(error => {
                    console.log(error);
                    document.getElementById("error-message").textContent = "متاسفانه خطایی رخ داده است"

                });
        }


    };
    const setSuccessMessage = (message) => {
        dispatch({successMessage, type: "MSG_AFTER_PASS_CHANGE"});
    };
    const handleCurrentPass = (event) => {
        // console.log("passValue", event.target.value)
        setCurrentPass(event.target.value)

    };
    const handleChangePass = (event) => {
        // console.log("passValue", event.target.value)
        setPassword(event.target.value)

    };
    const handleConfirmPass = (event) => {
        // console.log("passValue", event.target.value)
        confirmPassword(event.target.value)

    };
    const setChangePassStatus = (showChangePasswordPage) => {
        dispatch({showChangePasswordPage, type: 'SHOW_CHANGE_PASS_PAGE'})
    }
    const handleChangePassPage = () => {
        setChangePassStatus(true);
    };
    const handleChangeCurrentPass = () => {
        setChangePassStatus(true);
    };
    const goBack = () => {
        setChangePassStatus(false)
    };
    return (
        <React.Fragment>
            <div>
                <FontAwesomeIcon color="white" onClick={goBack} icon={faArrowCircleLeft}/>
            </div>
            <div className="row">
                <div className="col-sm-9 col-md-7 col-lg-5 mx-auto">
                    <div className="card card-change my-5">
                        <div className="card-body">
                            <h5 className="card-title text-center">Change Your Password</h5>

                            <form className="form-change">

                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputCurrentPass"
                                        placeholder="Current password"
                                        onChange={handleCurrentPass}
                                        onKeyDown={handleKeyDown}
                                        required
                                    />
                                    <label htmlFor="inputCurrentPass">Current password</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputNewPass"
                                        placeholder="New password"
                                        onChange={handleChangePass}
                                        onKeyDown={handleKeyDown}
                                        required
                                    />
                                    <label htmlFor="inputNewPass">Password</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputConfirm"
                                        placeholder="Confirm password"
                                        onChange={handleConfirmPass}
                                        onKeyDown={handleKeyDown}
                                        required
                                    />
                                    <label htmlFor="inputConfirm">Confirm Password</label>

                                </div>
                                <button
                                    type="submit"
                                    className="btn btn-lg btn-primary btn-block text-uppercase"
                                    id="changePass"
                                    onClick={handleSubmit}
                                >Change Password
                                </button>

                            </form>
                        </div>
                    </div>
                </div>
                <p id="error-message"></p>

            </div>
        </React.Fragment>
    );

}


export default ChangePassword;
