import React from "react";
import {useDispatch, useSelector} from "react-redux";
import Dropdown from 'react-bootstrap/Dropdown'
import "./style.css"
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Button from "react-bootstrap/Button";
const DropDownMenu = () => {
    const status = useSelector(state => state.status);
    const $pres = useSelector(state => state.$pres);
    const connection = useSelector(state => state.connection);
    const dispatch = useDispatch();

    const setStatus = (s) => {
        connection.send($pres().c("show").t(s));
        dispatch({s, type: 'MY_STATUS'});
    };

    return (
        <React.Fragment>
            <Dropdown as={ButtonGroup}>

                <Button variant="success">{status === "dnd" ? "do not disturb" : status }</Button>

                <Dropdown.Toggle split variant="success" id="dropdown-split-basic" />

                <Dropdown.Menu>
                    <Dropdown.Item onClick={() => setStatus("chat")} href="#/action-1">online</Dropdown.Item>
                    <Dropdown.Item onClick={() => setStatus("away")} href="#/action-2">away</Dropdown.Item>
                    <Dropdown.Item onClick={() => setStatus("dnd")} href="#/action-3">do not disturb</Dropdown.Item>
                    <Dropdown.Item onClick={() => setStatus("xa")} href="#/action-3">Away for long time</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>


        </React.Fragment>
    );
}


export default DropDownMenu;
