import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";

const RosterStatusDetector = () => {
    const connection = useSelector(state => state.connection)
    const rosterStatus = useSelector(state => state.rosterStatus);
    const [rosterStatusColor, setRosterStatusColor] = useState('chat');

    useEffect(() => {
        setRosterStatusColor(rosterStatus)
        console.log("test")

    }, [rosterStatus]);

    return (
        <React.Fragment>
            <span
                className={rosterStatusColor === "xa" ? "offline" : rosterStatusColor}></span>
        </React.Fragment>
    );
}


export default RosterStatusDetector;
