const initialState = {
    status: "chat",
    connection: new window.Strophe.Connection(
        "http://77.104.116.225:5280/http-bind"
    ),
    $pres: window.$pres,
    $iq: window.$iq,
    $msg: window.$msg,
    strophe: window.Strophe,
    showMainPage: false,
    contacts: [],
    rosterStatus: [{
        id: 'mehrdad@ejabberd/587271492899195569722245',
        status: 'chat'
    }],
    selectedContact: '',
    rosterClicked: false

};
const reducer = (state = initialState, action) => {
    console.log("connection", state.connection)

    switch (action.type) {
        case 'MY_STATUS': {
            return {
                ...state,
                status: action.s
            };
        }
        case 'SHOW_MAIN_PAGE': {
            return {
                ...state,
                showMainPage: action.showMainPage
            };
        }
        case 'GET_CONTACTS': {
            return {
                ...state,
                contacts: action.contacts
            };
        }
        case 'CHANGE_EACH_ROSTER_STATUS': {
            return state.rosterStatus.map((item, index) => {
                if (item.id === action.rosterStatusItem.id) {
                    // This isn't the item we care about - keep it as-is
                    return {...item, ...action.rosterStatusItem}
                }
                return item
            })

        }
        case 'SET_SELECTED_ROSTER': {

            return {
                ...state,
                selectedContact: action.contact
            };
        }
        case 'CHECK_ROSTER_CLICK': {

            return {
                ...state,
                rosterClicked: action.rosterClicked
            };
        }
        default:
            return state;
    }
};
export default reducer;
