import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";

const RosterStatusDetector = (props) => {
    const rosterStatus = useSelector(state => state.rosterStatus);
    const [rosterStatusColor, setRosterStatusColor] = useState('chat');

    useEffect(() => {
        setRosterStatusColor(rosterStatus);
    }, [rosterStatus]);

    return (
        <React.Fragment>
            <span className={props.status === "xa" ? "offline" : props.status}></span>
        </React.Fragment>
    );
}


export default RosterStatusDetector;
