import React from "react";
import {useSelector} from "react-redux";
import ContactsList from "../contacts-list";
import ChatPanel from "../chat-panel";
import CallInformation from "../../call/call-information";
import "./style.css";
import {faPhone, faComment, faCommentSlash} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faComments} from "@fortawesome/free-solid-svg-icons/faComments";
import InnerMenu from "../../call/inner-menu";

const LeftNav = () => {
    const selectedContact = useSelector(state => state.selectedContact);

    return (
        <React.Fragment>
            <div className="row">
                <div className="col-1 menu">
                    <div className="nav flex-column nav-pills" id="v-pills-tab" role="tablist"
                         aria-orientation="vertical">
                        <a className="nav-link active" id="v-pills-chat-tab" data-toggle="pill" href="#v-pills-chat"
                           role="tab" aria-controls="v-pills-chat" aria-selected="true">
                            <FontAwesomeIcon color="white" icon={faComments}/>
                            Chat</a>
                        <a className="nav-link" id="v-pills-call-tab" data-toggle="pill" href="#v-pills-call"
                           role="tab" aria-controls="v-pills-call" aria-selected="false">
                            <FontAwesomeIcon color="white" icon={faPhone}/>
                            Call</a>
                    </div>
                </div>
                <div className="col-11 tab-panel">

                    <div className="tab-content" id="v-pills-tabContent">
                        <div className="tab-pane fade show active" id="v-pills-chat" role="tabpanel"
                             aria-labelledby="v-pills-chat-tab">
                            <ContactsList/>
                            {
                                selectedContact !== '' ? <ChatPanel/> :
                                    <span className="default-text">Select a contact to start messaging...</span>
                            }

                        </div>
                        <div className="tab-pane fade" id="v-pills-call" role="tabpanel"
                             aria-labelledby="v-pills-call-tab">
                            <div className="inner-menu">
                                <InnerMenu/>
                            </div>
                            <div className="detail-content">
                                <CallInformation/>
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        </React.Fragment>
    )
};
export default LeftNav;
