import React from "react";
import {useSelector,} from "react-redux";
import avatarImage from "../../../images/avatar.jpg";
import LogItem from "../log-item";
import "./style.css"

const CallHistoryDetail = () => {
    const cdrsData = useSelector(state => state.cdrsData);
    const selectedLogContact = useSelector(state => state.selectedLogContact);

    const renderLogs = (group) => {
        return (
            Object.values(group).map((myItem, i) => (
                (myItem.direction === "inbound" ?
                    myItem.to : myItem.from) === selectedLogContact ?
                    myItem !== null ? Object
                        .keys(group)
                        .map((key, index) => (
                            <div className={index + "-group"} key={index}>
                                <p>{key.replace('Logs', '')}</p>
                                {group[key].map((item, i) => (
                                    (item.direction === "inbound" ?
                                        item.to : item.from) === selectedLogContact ?
                                        item !== null ? <LogItem item={item} number={i}/> : null
                                        : null
                                ))}
                            </div>
                        )) : null
                    : null
            ))
        )

    };

    return (

        <React.Fragment>
            <div className="container">
                <div className="user-image">

                    <img alt="avatar" src={avatarImage}/>
                </div>
                <div className="userName">
                    {selectedLogContact}
                </div>
                <div>
                    {
                        renderLogs(cdrsData)
                    }
                </div>
            </div>
        </React.Fragment>
    )
};


export default CallHistoryDetail;
