import React, {useEffect, useState} from "react";
import avatarImage from "../../images/avatar.jpg";
import "./style.css";
import {useSelector} from "react-redux";

const ContactItem = () => {
    const contacts = useSelector(state => state.contacts);
    const rosterStatus = useSelector(state => state.rosterStatus);
    const [rosterStatusColor, setRosterStatusColor] = useState('invisible');

    useEffect(() => {

        setRosterStatusColor()
        console.log(rosterStatus)
    }, rosterStatus);


    return (
        <div
            className="row item"
            id={contacts.jid}
        >
            <div className="avatar">
                <span className={rosterStatusColor === "invisible" ? "offline" : rosterStatusColor}></span>
                <img alt="avatar" src={avatarImage}/>
            </div>
            <div className="right-side">
                <div className="extension">
                    <p>112</p>
                </div>

                <div className="contact-name">
                    <p>{contacts.name}</p>
                </div>
            </div>
        </div>
    );
}


export default ContactItem;
