import React, {useEffect, useState} from "react";
import MessagePopUp from "../message-popup";
import "./style.css";
import $ from "jquery";
import {useSelector} from "react-redux";

const ChatBox = () => {
    const selectedContact = useSelector(state => state.selectedContact);
    const rosterStatus = useSelector(state => state.rosterStatus);
    const $iq = useSelector(state => state.$iq);
    const $msg = useSelector(state => state.$msg);
    const connection = useSelector(state => state.connection);
    // const showMainPage = useSelector(state => state.showMainPage);
    const [historyMessages, setHistoryMessages] = useState({item: []});
    const [allMessages, setAllMessages] = useState({item: []});
    const [recMessages, setRecMessages] = useState({});
    const [messageText, changeMessageText] = useState("");
    const [lastActivity, setLastActivity] = useState({from: '', time: ''});

    const scrollToBottom = () => {
        let chatDiv = document.querySelector(".all-messages");
        chatDiv.scrollTo(0, chatDiv.scrollHeight);
        console.log("hi")
    };
    useEffect(() => {
        getLog();
    }, [selectedContact]);

    const getLog = () => {

        let sentMessages,
            myRecMessages,
            history = [],
            archiveIq = $iq({
                type: "set",
                id: "archive1"

            }).c("query", {
                xmlns: "urn:xmpp:mam:2"
            }).c("set", {xmlns: "http://jabber.org/protocol/rsm"});

        connection.mam.query(connection.send(archiveIq), {
            with: selectedContact,
            onMessage: function (message) {
                $(message).each(function () {
                    if (
                        $(this)
                            .find("forwarded message")
                            .attr("from")
                            .split("/", 1)[0] === selectedContact
                    ) {
                        myRecMessages = {
                            text: $(this)
                                .find("forwarded message body")
                                .text(),
                            stamp: $(this)
                                .find("delay")
                                .attr("stamp"),
                            type: "rec"
                        };
                        history = history.concat(myRecMessages);
                    } else if (
                        $(this)
                            .find("forwarded message")
                            .attr("from")
                            .split("/", 1)[0] !== selectedContact
                    ) {
                        sentMessages = {
                            text: $(this)
                                .find("forwarded message body")
                                .text(),
                            stamp: $(this)
                                .find("delay")
                                .attr("stamp"),
                            type: "sent"
                        };

                        history = history.concat(sentMessages);
                    }
                });
                return true;
            },
            onComplete: function (response) {
                setHistoryMessages(history)
                scrollToBottom();
            }
        });
    };

    const handleChangeMsg = (event) => {
        changeMessageText(event.target.value)
    };

    const handleSubmit = () => {
        console.log("selectedContact", selectedContact);
        let txtJID = selectedContact,
            txtMsg = messageText,
            message;

        if (txtMsg === undefined || txtMsg === "")
            alert("Empty Message, please type something");
        else {
            message = {
                to: txtJID,
                message: txtMsg,
                time: new Date(),
                type: "sent"
            };
            console.log("txtMsg", txtMsg);
            sendMessage(message);
            console.log("type", typeof (allMessages))

        }
        connection.addHandler(
            onMessage,
            null,
            "message",
            null,
            null,
            null
        );
        scrollToBottom();
    };

    //Sending message to contact
    const sendMessage = (msg) => {
        let reply = $msg({
            to: msg.to,
            from: connection.jid,
            type: "chat",
            id: "message-1"
        })
            .c("body")
            .t(msg.message).up().c("thread").t("salam");
        connection.send(reply.tree());

        let sentArray = allMessages.item;
        sentArray.push(msg);
        setAllMessages({
            ...allMessages,
            item: sentArray
        });
        console.log("allmessages", allMessages)

    };

    const listMessages = () => {

        return (
            allMessages.item.length === 0 ? '' : allMessages.item.map((item, i) => {
                return (
                    <MessagePopUp
                        key={i}
                        sentMsg={item.message}
                        type={item.type}
                    />
                );
            }))

    };
    const onMessage = (msg) => {
        let from = msg.getAttribute("from");
        let type = msg.getAttribute("type");
        let elems = msg.getElementsByTagName("body");
        if (type === "error") {
            alert("An error occured! ");
            return;
        }


        if (type === "chat") {
            let message = {
                from: from,
                message: elems[0].innerHTML,
                time: new Date(),
                type: "rec"
            };

            let recArray = allMessages.item;
            console.log("recArray1", recArray)

            recArray.push(message);
            console.log("recArray2", recArray)

            setAllMessages({
                ...allMessages,
                item: recArray
            });
            console.log("setAllMessages", allMessages)


        }
        return true; //The handler should return true if it is to be invoked again; returning false will remove the handler after it returns
    };

    /*----------------RECENT ACTIVITY------------------*/
    const onLastActivity = (iq) => {

        let from = $(iq).attr("from"); // the jabber_id of the contact\+
        let lastActivityObj;
        let errorType = $(iq).attr('type');
        $(iq).find("query").each(function () {

            if (errorType === "result") {
                let lastActivityTime = $(iq).find("query").attr('seconds');
                lastActivityObj = {
                    from: from,
                    time: lastActivityTime,
                    type: errorType,
                    errorMessage: null
                };
                setLastActivity({
                    ...lastActivity, from: lastActivityObj.from,
                    time: lastActivityObj.time,
                    type: lastActivityObj.type,
                    errorMessage: lastActivityObj.errorMessage
                });
            }
            if (errorType === "error") {
                let errorMessage = $(iq).find("text").text();
                lastActivityObj = {
                    from: from,
                    time: null,
                    type: errorType,
                    errorMessage: errorMessage
                };
                setLastActivity({
                    ...lastActivity,
                    from: lastActivityObj.from,
                    time: lastActivityObj.time,
                    type: lastActivityObj.type,
                    errorMessage: lastActivityObj.errorMessage
                });

            }

        });
        return true

    };
    const getLastActivity = () => {
        const iq = $iq({
            type: "get",
            from: connection.jid,
            to: selectedContact,
            id: "last1"
        }).c("query", {xmlns: "jabber:iq:last"});
        connection.sendIQ(iq);
        connection.addHandler(onLastActivity, null, "iq", null, null, null);
    };
    useEffect(() => {
        getLastActivity();
    }, [selectedContact]);

    useEffect(() => {
        getLastActivity();
    }, [rosterStatus]);

    return (
        <React.Fragment>
            <div className="userInfo-bar">
                <p className="userId">{selectedContact}</p>
                <p>{lastActivity.type === "error" ? "Last seen recently" : lastActivity.time === "0" ? "online"
                    : lastActivity.time < 60 ? "last seen " + lastActivity.time + " seconds ago"
                        : Math.floor(lastActivity.time / 60) < 60 ? "last seen " + Math.floor(lastActivity.time / 60) + " minutes ago"
                            : Math.floor(lastActivity.time / 3600) < 24 ? "last seen " + Math.floor(lastActivity.time / 3600) + " hours ago"
                                : Math.floor(lastActivity.time / 86400) < 7 ? "last seen " + Math.floor(lastActivity.time / 86400) + " days ago" : "last seen over a week ago"
                }</p>
            </div>

            {/*<div className="search-bar">*/}
            {/*    <Search items={historyMessages}/>*/}
            {/*</div>*/}

            <div className="all-messages">
                <div className="archive">
                    {historyMessages.map((item, i) => {
                        return (
                            <MessagePopUp
                                key={i}
                                sentMsg={item.text}
                                type={item.type}
                            />
                        );
                    })}
                </div>
                <div className="messages">
                    {listMessages()}
                </div>
            </div>

            <div className="textbox">
                <input
                    type="text"
                    id="msg-here"
                    className="form-control"
                    placeholder="Message here..."
                    rows="4"
                    value={messageText}
                    onChange={handleChangeMsg}
                />

                <button
                    type="button"
                    value="send"
                    className="primary"
                    id="sendButton"
                    onClick={handleSubmit}
                >send
                </button>
            </div>
        </React.Fragment>
    );
}


export default ChatBox;
