import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import Device from "../Device";
import Extension from "../extension";
import CallLogs from "../call-log";
import Modal from "../../modal";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSpinner} from "@fortawesome/free-solid-svg-icons";
import MakeCall from "../make-call";
import RegisterDevice from "../register-device";
import {getCookie} from "../../helper/functions";
import axios from "axios";
import "./style.css"

const CallInformation = () => {
    const loading = useSelector(state => state.loadingStatus);
    const showMainPage = useSelector(state => state.showMainPage);
    const [loadingStatus, setLoadingStatus] = useState(true);
    const callFlowsData = useSelector(state => state.callFlowsData);
    const showCallPage = useSelector(state => state.showCallPage);


    const dispatch = useDispatch();
    useEffect(() => {
        setLoadingStatus(false);
        dispatch({loadingStatus, type: "SET_LOADING_STATUS"});
    }, [callFlowsData]);

    //Show Or hide Main page.
    const setShowCallApp = (showPage) => {
        console.log("showPage", showPage)
        dispatch({showPage, type: 'SHOW_CALL_PAGE'})
    };
    const delete_cookie = function(name) {
        document.cookie = name + '=;expires=Thu, 01 Jan 1970 00:00:01 GMT;';
    };
    const handleSubmit = () => {
        delete_cookie("monster_token");
        setShowCallApp(false)
    };

    return (

        <React.Fragment>
            <div className="container">
                <button
                    type="button"
                    className="btn btn-primary"
                    id="connect"
                    value="Logout"
                    onClick={handleSubmit}
                >Log out From Kazoo
                </button>

                {/*<div><Modal/></div>*/}
                <div><MakeCall/></div>
                <div>{
                    loading ?
                        <div className="call-spinner"><FontAwesomeIcon className="fas fa-spinner fa-spin"
                                                                       icon={faSpinner}/>
                        </div> :
                        <div className="info">
                            <Device/>
                            <Extension/>
                            <CallLogs/>
                        </div>
                }</div>
            </div>
        </React.Fragment>
    )
};


export default CallInformation;
