// import FormControlLabel from '@material-ui/core/FormControlLabel';
import {useDispatch, useSelector} from "react-redux";
import {RadioGroup, RadioButton} from 'react-radio-buttons'

const RadioButtonList = () => {
    const status = useSelector(state => state.status)
    const $pres = useSelector(state => state.$pres)
    const connection = useSelector(state => state.connection)
    const dispatch = useDispatch();

    const setStatus = (s) => {
        console.log("setStatus: " + s);
        connection.send($pres().c("show").t(s));
        dispatch({s, type: 'MY_STATUS'})
    }

    const handleChange = (event) => {
        // setStatus(event.target.innerHTML)
        console.log("event.target",event.target.value)

    }

    return (
        <React.Fragment>
            <RadioGroup onChange={handleChange}>
                <RadioButton value="online">
                    <p className="status">Online</p>
                </RadioButton>
                <RadioButton value="away">
                    Away
                    <p className="status">Away</p>

                </RadioButton>
                <RadioButton value="dnd">
                    <p className="status">Do Not Disturb</p>

                </RadioButton>
                <RadioButton value="invisible">

                    <p className="status">Invisible</p>

                </RadioButton>
            </RadioGroup>

            {/*<FormControl component="fieldset">*/}
