import React from "react";
import styles from "./style.module.css"
import avatarImage from "../../../images/avatar.jpg";
import {useSelector} from "react-redux";

const ContactItem = (props) => {
    const contacts = useSelector(state => state.contactsList)

    const data = contacts.reduce((r, e) => {
        // get first letter of name of current element
        let group = e.name[0];
        // if there is no property in accumulator with this letter create it
        if(!r[group]) r[group] = {group, children: [e]}
        // if there is push current element to children array for that letter
        else r[group].children.push(e);
        // return accumulator
        return r;
    }, {});
    console.log("contacts data", data)

    return (

        <React.Fragment>
            <div className={styles.contactItem + " col-4"}>
                <img className={styles.avatar} alt="avatar" src={avatarImage}/>
                <div className={styles.right}>
                    <span className={styles.name}>{props.item.name}</span>
                    <span className={styles.number}>{props.item.internal_number}</span>
                    <span className={styles.jobTitle}></span>
                    <span>Call</span>
                </div>
            </div>
        </React.Fragment>
    )
};
export default ContactItem;
