import React, {useEffect, useState} from "react";
import MessagePopUp from "../message-popup";
import "./style.css";
import $ from "jquery";
import {useSelector} from "react-redux";
import Search from "../search/search";

const ChatBox = () => {
    const selectedContact = useSelector(state => state.selectedContact);
    const $iq = useSelector(state => state.$iq);
    const $msg = useSelector(state => state.$msg);
    const connection = useSelector(state => state.connection);
    const showMainPage = useSelector(state => state.showMainPage);
    const [historyMessages, setHistoryMessages] = useState([]);
    const [allMessages, setAllMessages] = useState([]);
    const [messageText, changeMessageText] = useState("");
    const sendLastActivity = useSelector(state => state.sendLastActivity);
    const [lastActivity, setLastActivity] = useState({from: '', time: ''});

    useEffect(() => {
        getLog();
    }, [selectedContact]);

    const getLog = () => {

        let sentMessages,
            recMessages,
            history = [],
            archiveIq = $iq({
                type: "set",
                id: "archive1"

            }).c("query", {
                xmlns: "urn:xmpp:mam:2"
            }).c("set", {xmlns: "http://jabber.org/protocol/rsm"}).c('max').t('5');
        connection.mam.query(connection.send(archiveIq), {
            with: selectedContact,
            onMessage: function (message) {
                $(message).each(function () {
                    if (
                        $(this)
                            .find("forwarded message")
                            .attr("from")
                            .split("/", 1)[0] === selectedContact
                    ) {
                        recMessages = {
                            text: $(this)
                                .find("forwarded message body")
                                .text(),
                            stamp: $(this)
                                .find("delay")
                                .attr("stamp"),
                            type: "rec"
                        };
                        history = history.concat(recMessages);
                    } else if (
                        $(this)
                            .find("forwarded message")
                            .attr("from")
                            .split("/", 1)[0] !== selectedContact
                    ) {
                        sentMessages = {
                            text: $(this)
                                .find("forwarded message body")
                                .text(),
                            stamp: $(this)
                                .find("delay")
                                .attr("stamp"),
                            type: "sent"
                        };

                        history = history.concat(sentMessages);
                    }
                });

                return true;
            },
            onComplete: function (response) {
                setHistoryMessages(history);
                let chatDiv = document.querySelector(".all-messages");
                chatDiv.scrollTo(0, chatDiv.scrollHeight);
            }
        });
    }


    const handleChangeMsg = (event) => {
        changeMessageText(event.target.value)
    };


    const handleSubmit = (e) => {
        e.preventDefault();
        let txtJID = selectedContact,
            txtMsg = messageText,
            currentMessages = [],
            message;
        console.log("message1", message)

        console.log("txtJID", txtJID);
        console.log("txtMsg", txtMsg);

        if (txtMsg === undefined || txtMsg === "")
            alert("Empty Message, please type something");
        else {
            message = {
                to: txtJID,
                message: txtMsg,
                time: new Date(),
                type: "sent"
            };
            currentMessages = currentMessages.concat(message)

            setAllMessages(allMessages.splice(0, 0, message));
            sendMessage(message);
            console.log("message", messageText)
            console.log("currentMessages", currentMessages)
            console.log("allmessages", allMessages)
        }
        connection.addHandler(
            onMessage,
            null,
            "message",
            null,
            null,
            null
        );
    };

    //Sending message to contact
    const sendMessage = (msg) => {
        let reply = $msg({
            to: msg.to,
            from: connection.jid,
            type: "chat",
            id: "event1"
        })
            .c("body")
            .t(msg.message)
            .up()
            .c("request", {xmlns: "urn:xmpp:receipts"});

        connection.send(reply.tree());
    }

    const onMessage = (msg) => {
        let from = msg.getAttribute("from");
        let type = msg.getAttribute("type");
        let elems = msg.getElementsByTagName("body");
        if (type === "error") {
            alert("An error occured! ");
            return;
        }

        if (type === "chat") {
            let message = {
                from: from,
                message: elems[0].innerHTML,
                time: new Date(),
                type: "rec"
            };
            console.log("rec message", message)
            setAllMessages(allMessages.splice(0, 0, message));

        }

        return true; //The handler should return true if it is to be invoked again; returning false will remove the handler after it returns
    }


    /*----------------RECENT ACTIVITY------------------*/
    const onLastActivity = (iq) => {

        let from = $(iq).attr("from"); // the jabber_id of the contact\+
        let lastActivityObj;
        let errorType = $(iq).attr('type');
        console.log("errorType", errorType);

        $(iq).find("query").each(function () {

            if (errorType === "result") {
                let lastActivityTime = $(iq).find("query").attr('seconds');
                lastActivityObj = {
                    from: from,
                    time: lastActivityTime
                };
                console.log("lastActivityObj", lastActivityObj);
                setLastActivity({...lastActivity, from: lastActivityObj.from, time: lastActivityObj.time});
            }
            if (errorType === "error") {
                let errorMessage = $(iq).find("text").text();
                console.log("errorMessage", errorMessage)
            }

        });
        return true

    };
    const getLastActivity = () => {
        const iq = $iq({
            type: "get",
            from: connection.jid,
            to: selectedContact,
            id: "last1"
        }).c("query", {xmlns: "jabber:iq:last"});
        connection.sendIQ(iq);
        connection.addHandler(onLastActivity, null, "iq", null, null, null);

        console.log("iq", iq)
    };
    useEffect(() => {
        getLastActivity();
    }, [selectedContact]);


    return (
        <React.Fragment>
            <p className="userId">To : {selectedContact}</p>
            <p>{lastActivity.time}</p>
            <div className="search-bar">
                <Search items={historyMessages}/>
            </div>
            <div className="all-messages">

                <div className="messages">
                    {allMessages !== [] ? <MessagePopUp
                        type={allMessages.type}
                        sentMsg={allMessages.message}
                    /> : null}


                </div>
            </div>

            <div className="textbox">
                <input
                    type="text"
                    id="msg-here"
                    className="form-control"
                    placeholder="Message here..."
                    rows="4"
                    onChange={handleChangeMsg}
                />

                <button
                    type="button"
                    value="send"
                    className="primary"
                    id="sendButton"
                    onClick={handleSubmit}
                >send
                </button>
            </div>
        </React.Fragment>
    );
}


export default ChatBox;
