import React, {useState} from "react";
import axios from "axios";
import {getCookie} from "../../helper/functions";
import "./style.css"

const ChangePassword = () => {
    const [password, setPassword] = useState('');
    const [newPass, confirmPassword] = useState('');
    const [email, getEmail] = useState('');
    const [firstName, getFirstName] = useState('');
    const [lastName, getLastName] = useState('');

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            handleSubmit(event);
        }

    };
    const handleSubmit = () => {

        axios.post("http://77.104.116.187:8000/v2/accounts/" + getCookie("accID") + "/users/" + getCookie("owID"), {
            headers: {
                "X-Auth-Token": getCookie("monster_token")
            }
        })
            .then(response => {

            })
            .catch(error => {
                console.log(error);

            });
    };
    const handleChangePass = (event) => {
        // console.log("passValue", event.target.value)
        setPassword(event.target.value)

    };
    const handleConfirmPass = (event) => {
        // console.log("passValue", event.target.value)
        confirmPassword(event.target.value)

    };
    const handleEmail = (event) => {
        // console.log("passValue", event.target.value)
        getEmail(event.target.value)
    };
    const handleFirstName = (event) => {
        // console.log("passValue", event.target.value)
        getFirstName(event.target.value)
    };
    const handleLastName = (event) => {
        // console.log("passValue", event.target.value)
        getLastName(event.target.value)
    };
    return (
        <React.Fragment>
            <div className="row">
                <div className="col-sm-9 col-md-7 col-lg-5 mx-auto">
                    <div className="card card-change my-5">
                        <div className="card-body">
                            <form className="form-change">
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputEmail"
                                        placeholder="Email"
                                        // onChange={}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputEmail">First name</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputLastName"
                                        placeholder="Last name"
                                        // onChange={}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputLastName">Last name</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputFirstName"
                                        placeholder="First name"
                                        // onChange={}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputFirstName">First name</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputNewPass"
                                        placeholder="New password"
                                        onChange={handleChangePass}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputNewPass">Password</label>
                                </div>
                                <div className="form-label-group">
                                    <input
                                        className="form-control"
                                        size="col-sm-10"
                                        type="text"
                                        id="inputConfirm"
                                        placeholder="Confirm password"
                                        onChange={handleConfirmPass}
                                        onKeyDown={handleKeyDown}
                                        required autoFocus
                                    />
                                    <label htmlFor="inputConfirm">Confirm Password</label>

                                </div>
                                <button
                                    type="submit"
                                    className="btn btn-lg btn-primary btn-block text-uppercase"
                                    id="changePass"
                                    onClick={handleSubmit}
                                >Change Password
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    );

}


export default ChangePassword;
