import React, {useEffect, useState} from "react";
import Input from "../input";
import Button from "../button";
import {useDispatch, useSelector} from "react-redux";
import $ from "jquery";

const LoginPage = () => {
    const connection = useSelector(state => state.connection)
    const $pres = useSelector(state => state.$pres)
    const $iq = useSelector(state => state.$iq)
    const strophe = useSelector(state => state.strophe)
    const showMainPage = useSelector(state => state.showMainPage)
    const [jidValue, setJidValue] = useState('')
    const [passValue, setPassValue] = useState('')
    const [loginButton, changeLoginButton] = useState('connect')
    const [rosters, getRosters] = useState('')
    const dispatch = useDispatch();

    //strophe connection process
    const onConnect = (status) => {
        console.log("window.Strophe.Status", strophe.Status);
        if (status === strophe.Status.CONNECTING) {
            console.log("Strophe is connecting.");
        } else if (status === strophe.Status.CONNFAIL) {
            console.log("Strophe failed to connect.");
            setShowStatus(false)
        } else if (status === strophe.Status.AUTHENTICATING) {
            console.log("Strophe is AUTHENTICATING.");
        } else if (status === strophe.Status.AUTHFAIL) {
            console.log("Strophe is AUTHFAIL.");
            setShowStatus(false)
        } else if (status === strophe.Status.DISCONNECTING) {
            console.log("Strophe is disconnecting.");
        } else if (status === strophe.Status.DISCONNECTED) {
            console.log("Strophe is disconnected.");
            setShowStatus(false)
        } else if (status === strophe.Status.CONNECTED) {
            console.log("Strophe is connected.");
            setShowStatus(true)
            getRoster();
            sendPriority();

        }
    }
    /*-----------------Get Contacts List (step1)----------------=*/
    const getRoster = () => {
        getRosters($iq({type: "get", id: "r1"})
            .c("query", {xmlns: "jabber:iq:roster"}))

        connection.sendIQ(this.state.iq, rosterReceived);
    }

    /*-----------------Get Contacts List (step1)----------------*/
    /*-----------------Received contacts Time (step2)----------------*/
    const rosterReceived = (iq) => {
        let contactJid;
        let rosterName;
        let myContacts = [];
        $(iq)
            .find("item")
            .each(function () {
                contactJid = $(this).attr("jid"); // The jabber_id of your contact
                if ($(this).attr("name")) {
                    rosterName = $(this).attr("name");
                } else {
                    rosterName = "";
                }
                // The jabber_id of your contact
                // You can probably put them in a unordered list and and use their jids as ids.
                myContacts = myContacts.concat({
                    name: rosterName,
                    jid: jid
                });
            });

        this.setState({
            contacts: mycontacts
        });
        console.log("mycontacts", this.state.contacts)
        this.state.connection.send(window.$pres());

        this.state.connection.addHandler(
            this.onPresence,
            null,
            "presence",
            null,
            null,
            null
        );
    }

    /*-----------------Received contacts (step2)----------------*/
    /*-----------------Get priority ----------------*/
    const sendPriority = () => {
        this.state.connection.send(
            this.state.$pres()
                .c("priority")
                .t("10")
                .up()
                .c("rap", {xmlns: "urn:xmpp:rap:0", ns: "urn:xmpp:jingle:apps:rtp:0", num: '5'})
        );
        this.state.connection.sendIQ(this.state.$iq({type: "get"})
            .c("query", {xmlns: "http://jabber.org/protocol/disco#info"}))
    }

    /*-----------------Get priority ----------------*/

    const setShowStatus = (s) => {
        console.log("show", s)
        dispatch({s, type: 'SHOW_MAIN_PAGE'})
    }

    const handleChangeJID = (event) => {
        // console.log("jidValue", event.target.value)
        setJidValue(event.target.value)
        console.log("jidValue", jidValue)

    }
    const handleChangePass = (event) => {
        // console.log("passValue", event.target.value)
        setPassValue(event.target.value)
        console.log("passValue", ...passValue)

    }

    const handleSubmit = (event) => {
        event.preventDefault();
        if (jidValue === undefined || jidValue === "") {
            alert("Blank JID");
        } else if (passValue === undefined || passValue === "")
            alert("Empty pass, please type password");
        else {
            if (loginButton === "connect") {
                changeLoginButton("disconnect")
                connection.connect(
                    jidValue,
                    passValue,
                    onConnect
                );
            } else {
                connection.disconnect();
                changeLoginButton("connect")

            }
        }
    }

    return (
        <React.Fragment>
            <div className="row">
                <div className="col">
                    <form>
                        <input
                            label="JID"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="text"
                            placeholder="enter your JID..."
                            onChange={handleChangeJID}
                            value={jidValue}
                        />
                        <input
                            label="Password"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="password"
                            placeholder="enter your password..."
                            onChange={handleChangePass}
                            value={passValue}
                        />

                        <button
                            type="button"
                            className="btn btn-primary"
                            id="connect"
                            value={loginButton}
                            onClick={handleSubmit}
                        >{loginButton}</button>
                    </form>
                </div>
            </div>
        </React.Fragment>
    );
}


export default LoginPage;
