import React from "react";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faArrowLeft, faArrowRight, faBan} from "@fortawesome/free-solid-svg-icons";
import {myTime} from "../../helper/functions";
import {useDispatch} from "react-redux";
import {useSelector} from "react-redux";

const LogItem = (props) => {
    const jalali = require('moment-jalaali');
    const moment = require('persian-date');
    const orgMoment = require('moment');
    const dispatch = useDispatch();
    const cdrsData = useSelector(state => state.cdrsData);
    const selectedLogContact = useSelector(state => state.selectedLogContact);

    const handleContactClick = (selectedLogContact) => {
        dispatch({selectedLogContact, type: "SET_SELECTED_LOG_CONTACT"});
    };
    return (
        <React.Fragment>
            {cdrsData.map((item, i) => {
                return (
                    (item.direction === "inbound" ?
                        item.to : item.from) === selectedLogContact ? (
                        <div id={item.direction === "inbound" ?
                            item.to : item.from}>
                            <div onClick={() => handleContactClick((props.item.direction === "inbound" ?
                                props.item.to : props.item.from))} className={(props.item.direction === "inbound" ?
                                props.item.to : props.item.from) + " call-info"}>
                                <div className="left-side">

                        <span className="direction">
                        {
                            props.item.direction === "inbound" ?
                                <FontAwesomeIcon color="orange" icon={faArrowRight}/> :
                                props.item.direction === "outbound" && props.item.hangup_cause !== "NO_ANSWER" ?
                                    <FontAwesomeIcon color="green" icon={faArrowLeft}/> :
                                    props.item.direction === "outbound" && props.item.hangup_cause === "NO_ANSWER" ?
                                        <FontAwesomeIcon color="red" icon={faBan}/> : null
                        }
                        </span>
                                    <div className="name">
                                        {
                                            props.item.direction === "inbound" ?
                                                props.item.callee_id_name + "(" + props.item.callee_id_number + ")" :
                                                props.item.direction === "outbound" && props.item.hangup_cause !== "NO_ANSWER" ?
                                                    props.item.caller_id_name + "(" + props.item.caller_id_number + ")" :
                                                    props.item.direction === "outbound" && props.item.hangup_cause === "NO_ANSWER" ?
                                                        props.item.caller_id_name + "(" + props.item.caller_id_number + ")" : null
                                        }

                                        <div className="duration">

                                            {
                                                props.item.direction === "inbound" ?
                                                    <span>Outgoing call, </span>
                                                    :
                                                    props.item.direction === "outbound" && props.item.hangup_cause !== "NO_ANSWER" ?
                                                        <span>Incoming call, </span>
                                                        :
                                                        props.item.direction === "outbound" && props.item.hangup_cause === "NO_ANSWER" ?
                                                            <span>Missed call, </span>
                                                            : null

                                            }
                                            {myTime(props.item.duration_seconds)}
                                        </div>
                                    </div>
                                </div>
                                <div className="date">

                                    {
                                        jalali.unix(props.item.unix_timestamp).format('HH:mm:ss')
                                    }

                                </div>
                            </div>

                        </div>
                    ) : null
                )
            })
            }
        </React.Fragment>
    )
};


export default LogItem;
