import React, {useEffect, useState} from "react";
import avatarImage from "../../images/avatar.jpg";
import "./style.css";
import {useDispatch, useSelector} from "react-redux";
import RosterStatusDetector from "../roster-status-detector";
import $ from "jquery";

const ContactItem = (props) => {
    const dispatch = useDispatch();
    const connection = useSelector(state => state.connection);
    const $iq = useSelector(state => state.$iq);
    const showMainPage = useSelector(state => state.showMainPage);
    const [lastActivity, setLastActivity] = useState('')
    const handleRosterClick = (contact) => {
        console.log(contact);
        dispatch({contact, type: "SET_SELECTED_ROSTER"});
    };
    const getLastActivity = () => {
        let iq = $iq({
            type: "get",
            from: connection.jid,
            to: props.item.jid,
            id: "last1"
        }).c("query", {xmlns: "jabber:iq:last"})
        connection.sendIQ(iq);
    };
    useEffect(() => {
        getLastActivity()
    }, [showMainPage]);
    const onLastActivity = (iq) => {
        let from = $(iq).attr("from"); // the jabber_id of the contact\+
        let contact = document.querySelectorAll('.item');
        console.log("iq", iq);

        contact.forEach((item) => {
            console.log("item", item);
            console.log("from", from);

            if (item.getAttribute('id') === from.split("/", 1)[0]) {
                console.log("text",
                    $(iq).find("query")
                        .getAttribute('seconds')

                )
            }

        });

        return true;

    };
    useEffect(() => {
        connection.addHandler(
            onLastActivity,
            null,
            "presence",
            null,
            null,
            null
        );

    }, [showMainPage]);
    return (
        <React.Fragment>
            <button onClick={getLastActivity}>last activity</button>
            <p>{}</p>
            <div
                onClick={() => handleRosterClick(props.item.jid)}
                className="row item"
                id={props.item.jid}
            >
                <div className="avatar">

                    <RosterStatusDetector/>
                    <img alt="avatar" src={avatarImage}/>
                </div>
                <div className="right-side">
                    <div className="extension">
                        <p>112</p>
                    </div>

                    <div className="contact-name">
                        <p>{props.item.name}</p>
                    </div>
                </div>
            </div>

        </React.Fragment>
    );

};


export default ContactItem;
