import React, {useEffect, useState} from "react";
import ContactItem from "../contact-item";
import "./style.css";
import {useDispatch, useSelector} from "react-redux";
import $ from "jquery";

const ContactsList = () => {
    const contacts = useSelector(state => state.contacts);
    const dispatch = useDispatch();
    const showMainPage = useSelector(state => state.showMainPage);
    const rosterStatus = useSelector(state => state.rosterStatus);
    const [rosterStatusColor, setRosterStatusColor] = useState(rosterStatus);
    const connection = useSelector(state => state.connection);
    /*----------get contact presence-----------*/
    //use presences from xmpp server
    const onPresence = (presence) => {
        console.log("onPresence");

        let presence_type = $(presence).attr("type"); // unavailable, subscribed, etc...
        let from = $(presence).attr("from"); // the jabber_id of the contact
        if (from !== connection.jid) {
            if (!presence_type) {
                changeRosterStatus("chat")
            }
            if (presence_type !== "error") {
                if (presence_type === "unavailable") {
                    // Mark contact as offline
                    changeRosterStatus("offline");

                    console.log(from + "contact as unavailable");

                } else {
                    let show = $(presence)
                        .find("show")
                        .text(); // this is what gives away, dnd, etc.
                    if (show === "chat") {
                        changeRosterStatus("chat");
                        // Mark contact as online
                        console.log(from + "contact as online");

                    } else if (show === "away") {
                        // etc...
                        changeRosterStatus("away");
                        console.log(from + "contact as away");

                    } else if (show === "dnd") {
                        // etc...
                        changeRosterStatus("dnd");
                        console.log(from + "contact as dnd");

                    } else if (show === "xa") {
                        // etc...
                        changeRosterStatus("xa");
                        console.log(from + "contact as xa");

                    }
                }
            }
        }

        return true; //The handler should return true if it is to be invoked again; returning false will remove the handler after it returns
    }
    /*---------------------------------*/


    useEffect(() => {
        setRosterStatusColor(rosterStatus)
    }, [rosterStatus]);

    const handleRosterClick = (contact) => {
        console.log(contact)
        dispatch({contact, type: "SET_SELECTED_ROSTER"});
    };

    //change each roster status.
    const changeRosterStatus = (rosterStatus) => {
        console.log("rosterStatus", rosterStatus);
        dispatch({rosterStatus, type: 'CHANGE_EACH_ROSTER_STATUS'})
    }
    /*---------------------------------*/
    useEffect(() => {
        connection.addHandler(
            onPresence,
            null,
            "presence",
            null,
            null,
            null
        );
    }, [showMainPage]);
    /*----------get contact presence-----------*/
    return (
        <React.Fragment>
            <div className="col-sm-4 contacts">
                <div className="header">
                    <p>لیست پیام ها</p>
                    <p>افراد آنلاین (۴ نفر)</p>
                </div>
                {contacts.map((item, i) => {
                    console.log(item)
                    // Return the element. Also pass key
                    return (
                        <ContactItem
                            key={i}
                            item={item}
                        />

                    );
                })}

            </div>
        </React.Fragment>
    );
}


export default ContactsList;
