import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import axios from "axios";
import {getCookie} from "../../helper/functions";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faArrowLeft, faArrowRight, faBan} from "@fortawesome/free-solid-svg-icons";
import IncomingCall from "../inconming-call";
import DialledCalls from "../dialed-call";
import MissedCall from "../missed-calls";

const CallLogs = () => {
        const cdrsData = useSelector(state => state.cdrsData);
        const callType = useSelector(state => state.callType);
        const moment = require('persian-date');
        const orgMoment = require('moment');
        const jalali = require('moment-jalaali');
        const [cdrData, getCdrData] = useState({item: []});
        const [todayCdr, getTodayCdr] = useState({item: []});
        const [inbounds, filterInbounds] = useState({item: []});
        const dispatch = useDispatch();
        let today = orgMoment();

        const setCallType = (callType) => {
            dispatch({callType, type: "SET_CALL_TYPE"});
            console.log("type", callType)

        };
        const inboundCalls = () => {
            setCallType("inbound");
        };
        const outboundCalls = () => {
            setCallType("outbound");
        };
        const missedCallsCalls = () => {
            setCallType("missed");
        };


        return (
            <React.Fragment>
                <div className="filter-by-time">
                    <button className="btn-sm badge-light" onClick={todayCalls}>Today</button>
                    <button className="btn-sm badge-light">This Week</button>
                    <button className="btn-sm badge-light">This Month</button>
                </div>
                <div className="filter-by-type">
                    <button className="btn-sm badge-light" onClick={inboundCalls}>Incoming Calls</button>
                    <button className="btn-sm badge-light" onClick={missedCallsCalls}>Missed Calls</button>
                    <button className="btn-sm badge-light" onClick={outboundCalls}>Dialed Calls</button>
                </div>
                <table className="table table-sm table-striped table-bordered table-hover">
                    <thead>
                    <tr>
                        <th scope="col">
                            <FontAwesomeIcon color="green" icon={faArrowLeft}/><br/>
                            <FontAwesomeIcon color="orange" icon={faArrowRight}/>
                        </th>
                        <th scope="col">Date/Time</th>
                        <th scope="col">From</th>
                        <th scope="col">To</th>

                        <th scope="col">Duration</th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                        callType === "inbound" ? <IncomingCall/> : callType === "outbound" ?
                            <DialledCalls/> : callType === "missed" ?
                                <MissedCall/> : cdrsData === undefined ? null : cdrsData.map((item, i) => {
                                    return (
                                        <tr key={i}>
                                            <td>
                                                {
                                                    item.direction === "inbound" ?
                                                        <FontAwesomeIcon color="green"
                                                                         icon={faArrowLeft}/> : item.direction === "outbound" ?
                                                        <FontAwesomeIcon color="orange" icon={faArrowRight}/> :
                                                        <FontAwesomeIcon color="red" icon={faArrowRight}/>

                                                }
                                            </td>
                                            <td>

                                                {
                                                    jalali.unix(item.unix_timestamp).format('jYYYY/jM/jD, HH:mm:ss')
                                                }

                                            </td>
                                            <td>
                                                {item.caller_id_name + "(" + item.caller_id_number + ")"}
                                            </td>
                                            <td>
                                                {item.callee_id_name + "(" + item.callee_id_number + ")"}
                                            </td>
                                            <td>
                                                {item.duration_seconds}
                                            </td>
                                        </tr>
                                    )

                                })
                    }
                    </tbody>
                </table>
            </React.Fragment>
        )

    }
;


export default CallLogs;
