import React, {useDebugValue, useEffect, useState} from "react";
import MessagePopUp from "../message-popup";
import "./style.css";
import $ from "jquery";
import {useDispatch, useSelector} from "react-redux";
import {fetchHistoryMessages} from "../../../redux/action";

const ChatBox = () => {
    const selectedContact = useSelector(state => state.selectedContact);
    const historyMessages = useSelector(state => state.history);
    const newMessage = useSelector(state => state.newMessage);
    const strophe = useSelector(state => state.strophe);
    const rosterStatus = useSelector(state => state.rosterStatus);
    const $iq = useSelector(state => state.$iq);
    const $msg = useSelector(state => state.$msg);
    const connection = useSelector(state => state.connection);
    const [recMessages, setRecMessages] = useState({item: []});
    const [messageText, changeMessageText] = useState({txt: ''});
    const [lastActivity, setLastActivity] = useState({from: '', time: ''});
    const dispatch = useDispatch();
    const contacts = useSelector(state => state.contacts);

    const [messageList, setMessageList] = useState({item: []});


    const scrollToBottom = () => {
        let chatDiv = document.querySelector(".all-messages");
        chatDiv.scrollTo(0, chatDiv.scrollHeight);
        console.log("hi")
    };
    useEffect(() => {
        getLog();
    }, [selectedContact]);

    const getLog = () => {
        let mySentMessages,
            myRecMessages,
            recHistory = [],
            sentHistory = [];

        connection.mam.query(connection.jid.split("/", 1)[0], {
            with: selectedContact,
            onMessage: function (message) {
                console.log("mam");
                $(message).each(function () {

                    if (
                        $(this)
                            .find("forwarded message")
                            .attr("from")
                            .split("/", 1)[0] === selectedContact
                    ) {
                        myRecMessages = {
                            text: $(this)
                                .find("forwarded message body")
                                .text(),
                            stamp: $(this)
                                .find("delay")
                                .attr("stamp"),
                            type: "rec"
                        };
                        recHistory = messageList.item;
                        recHistory.push(myRecMessages);
                        setMessageList({...messageList, item: recHistory});
                    } else if (
                        $(this)
                            .find("forwarded message")
                            .attr("to")
                            .split("/", 1)[0] === selectedContact
                    ) {
                        mySentMessages = {
                            text: $(this)
                                .find("forwarded message body")
                                .text(),
                            stamp: $(this)
                                .find("delay")
                                .attr("stamp"),
                            type: "sent"
                        };
                        sentHistory = messageList.item;
                        sentHistory.push(mySentMessages);
                        setMessageList({...messageList, item: sentHistory});
                    }


                });

                dispatch({historyMessages: sentHistory, type: "FETCH_HISTORY_MESSAGES"});

                return true;
            },
            onComplete: function (response) {
                scrollToBottom();

            }
        });
    };


    const handleSubmit = () => {

        let txtJID = selectedContact,
            txtMsg = messageText.txt,
            message,
            sents = [];

        if (txtMsg === undefined || txtMsg === "")
            alert("Empty Message, please type something");
        else {
            let reply = $msg({
                to: txtJID,
                from: connection.jid,
                type: "chat",
                id: "message-1"
            })
                .c("body")
                .t(txtMsg);
            connection.send(reply.tree());

            message = {
                to: txtJID,
                message: txtMsg,
                time: new Date(),
                type: "sent"
            };
            let newMsg = {
                text: txtMsg,
                stamp: '2019-11-10T13:05:04.053904Z',
                type: "sent"
            };

            const text = message.message
            dispatch({newMessage: text, type: 'GET_NEW_MESSAGE'})
            dispatch({newMessages: newMsg, type: 'PUSH_HISTORY_MESSAGES'});
        }

    };


    const onReceiveMessage = (msg) => {
        console.log("msg", msg)
        let from = msg.getAttribute("from");
        let type = msg.getAttribute("type");
        let elems = msg.getElementsByTagName("body");
        let recs;
        if (type === "error") {
            alert("An error occured! ");
            return;
        }
        if (type === "chat") {
            let message = {
                from: from,
                message: elems[0].innerHTML,
                time: new Date(),
                type: "rec"
            };
            let recMsg = {
                text: elems[0].innerHTML,
                stamp: '2019-11-10T13:05:04.053904Z',
                type: "rec"
            };
             dispatch({newMessages: recMsg, type: 'PUSH_HISTORY_MESSAGES'})
        }
        return true; //The handler should return true if it is to be invoked again; returning false will remove the handler after it returns
    };


    /*----------------RECENT ACTIVITY------------------*/
    const onLastActivity = (iq) => {

        let from = $(iq).attr("from"); // the jabber_id of the contact\+
        let lastActivityObj;
        let errorType = $(iq).attr('type');
        $(iq).find("query").each(function () {

            if (errorType === "result") {
                let lastActivityTime = $(iq).find("query").attr('seconds');
                lastActivityObj = {
                    from: from,
                    time: lastActivityTime,
                    type: errorType,
                    errorMessage: null
                };
                setLastActivity({
                    ...lastActivity, from: lastActivityObj.from,
                    time: lastActivityObj.time,
                    type: lastActivityObj.type,
                    errorMessage: lastActivityObj.errorMessage
                });
            }
            if (errorType === "error") {
                let errorMessage = $(iq).find("text").text();
                lastActivityObj = {
                    from: from,
                    time: null,
                    type: errorType,
                    errorMessage: errorMessage
                };
                setLastActivity({
                    ...lastActivity,
                    from: lastActivityObj.from,
                    time: lastActivityObj.time,
                    type: lastActivityObj.type,
                    errorMessage: lastActivityObj.errorMessage
                });

            }

        });
        return true

    };
    const getLastActivity = () => {
        const iq = $iq({
            type: "get",
            from: connection.jid,
            to: selectedContact,
            id: "last1"
        }).c("query", {xmlns: "jabber:iq:last"});
        connection.sendIQ(iq);
        connection.addHandler(onLastActivity, null, "iq", null, null, null);
    };
    useEffect(() => {
        getLastActivity();
    }, [selectedContact]);

    useEffect(() => {
        getLastActivity();
    }, [rosterStatus]);

    useEffect(() => {
        onReceiveMessage();
        connection.addHandler(onReceiveMessage, null, "message", null, null, null)
    }, [messageList]);

    useEffect(() => {
        console.log('*****************************************')
        setMessageList({
            ...messageList,
            item: historyMessages
        })
    }, [newMessage])

    const renderList = () => {
        return (
            messageList.item.length > 0 ? messageList.item.map((item, i) => {
                return (
                    <MessagePopUp
                        key={i}
                        sentMsg={item.text}
                        type={item.type}
                    />
                );
            }) : null
        )
    }
    return (
        <React.Fragment>
            <div className="userInfo-bar">
                <p className="userId">{selectedContact}</p>
                <p>{lastActivity.type === "error" ? "Last seen recently" : lastActivity.time === "0" ? "online"
                    : lastActivity.time < 60 ? "last seen " + lastActivity.time + " seconds ago"
                        : Math.floor(lastActivity.time / 60) < 60 ? "last seen " + Math.floor(lastActivity.time / 60) + " minutes ago"
                            : Math.floor(lastActivity.time / 3600) < 24 ? "last seen " + Math.floor(lastActivity.time / 3600) + " hours ago"
                                : Math.floor(lastActivity.time / 86400) < 7 ? "last seen " + Math.floor(lastActivity.time / 86400) + " days ago" : "last seen over a week ago"
                }</p>
            </div>

            {/*<div className="search-bar">*/}
            {/*    <Search items={historyMessages}/>*/}
            {/*</div>*/}

            <div className="all-messages">
                <div className="archive">

                    {
                        renderList()
                    }
                </div>

            </div>

            <div className="textbox">
                <input
                    type="text"
                    id="msg-here"
                    className="form-control"
                    placeholder="Message here..."
                    rows="4"
                    value={messageText.txt}
                    onChange={(event) => {
                        changeMessageText({
                            ...messageText,
                            txt: event.target.value
                        })
                    }}
                />

                <button
                    type="button"
                    value="send"
                    className="primary"
                    id="sendButton"
                    onClick={handleSubmit}
                >send
                </button>
            </div>
        </React.Fragment>
    );
}


export default ChatBox;
