import React from "react";
import $ from "jquery";

class StatusDetector extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            contactStatus: this.props.contactStatus
        };
    }

    componentDidUpdate(prevProps, prevState) {
        // only update chart if the data has changed
        if (prevProps.contactStatus !== this.props.contactStatus) {
            this.setState({
                contactStatus: this.props.contactStatus
            });
        }

    }

    render() {
        return (
            <React.Fragment>

                <span
                    className={this.state.contactStatus === "online" ? "online" :
                        this.state.contactStatus === "invisible" ? "invisible" :
                            this.state.contactStatus === "away" ? "away" :
                                "dnd"}>status</span>
            </React.Fragment>
        );
    }
}

export default StatusDetector;
