import React, {useEffect} from "react";
import {useSelector,} from "react-redux";
import avatarImage from "../../../images/avatar.jpg";
import LogItem from "../log-item";
import "./style.css"
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSpinner, faWindowClose} from "@fortawesome/free-solid-svg-icons";

const CallHistoryDetail = () => {
    const cdrsData = useSelector(state => state.cdrsData);
    const selectedLogContact = useSelector(state => state.selectedLogContact);

    const renderLogs = (group) => {
        return (
            Object
                .keys(group)
                .map((key, index) => (
                    <div className="group" key={index}>
                        <p>{key}</p>
                        {group[key].map((item, i) => (
                            (item.direction === "inbound" ?
                                item.to : item.from) === selectedLogContact ?
                                <LogItem item={item} number={i}/> : null
                        ))}
                    </div>
                ))
        );
    };

    return (
        <React.Fragment>
            <div className="container">
                <FontAwesomeIcon  icon={faWindowClose} color="#fff"/>

                <div className="user-image">

                    <img alt="avatar" src={avatarImage}/>
                </div>
                <div className="userName">
                    {selectedLogContact}
                </div>
                <div className="detail">
                    {
                        renderLogs(cdrsData)
                    }
                </div>
            </div>
        </React.Fragment>
    )
};


export default CallHistoryDetail;
