            //         from: from,
            //         time: seconds
            //     }
            //
            //     console.log("lastActivity",
            //         $(iq).find("query")
            //             .attr('seconds')
            //     );
            //
            //     let lastActivityTime = $(iq).find("query").attr('seconds')
            //     setLastActivity(lastActivityTime);
            // }


        });

        return true;

    };

    return (
        <React.Fragment>
            <button onClick={getLastActivity}>last activity</button>
            <p>{lastActivity}</p>
            <div
                onClick={() => handleRosterClick(props.item.jid)}
                className="row item"
                id={props.item.jid}
            >
                <div className="avatar">

                    <RosterStatusDetector/>
                    <img alt="avatar" src={avatarImage}/>
                </div>
                <div className="right-side">
                    <div className="extension">
                        <p>112</p>
                    </div>

                    <div className="contact-name">
                        <p>{props.item.name}</p>
                    </div>
                </div>
            </div>

        </React.Fragment>
    );

};


export default ContactItem;
