import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";
import LogItem from "../log-item";
import HistoryDetailLogs from "../history-detail-logs";

const GroupedLog = () => {
    const cdrsData = useSelector(state => state.cdrsData);
    const selectedLogContact = useSelector(state => state.selectedLogContact);
    const [grouped, setgrouped] = useState([]);
    const jalali = require('moment-jalaali');

    const _renderLogs = (group) => {
        console.log("cdr", cdrsData)
        return Object
            .keys(group)
            .map((key, index) => (
                <div key={index}>
                    <p>{key}</p>
                    {
                        // console.log("group[key]", group[key])
                        count(group[key])
                        // group[key].map((item, i) => (
                        //
                        //     // console.log("group[key]", group[key])
                        //     <LogItem item={item} key={i}/>
                        //
                        // ))
                    }

                </div>
            ));
    };
    const count = (array) => {
        let current = null;
        let cnt = 0;
        for (var i = 0; i < array.length; i++) {
            if (current !== null) {
                if (array[i].from != current.from && array[i].direction !== "inbound") {
                    if (cnt > 0) {
                        console.log(current + ' comes --> ' + cnt + ' times<br>');
                    }
                    current = array[i];
                    cnt = 1;
                } else {
                    cnt++;
                }
            }
        }
        if (cnt > 0) {
            console.log(current + ' comes --> ' + cnt + ' times');
        }

    }
    const clearDuplicates = (group) => {
        console.log("objjjjj", Object.values(group));
        return Object
            .values(group)
            .map((key, index) => {
                    const result = [...key.reduce((mp, o) => {
                        const key = JSON.stringify([o.direction, o.from]);
                        if (!mp.has(key)) mp.set(key, {...o, count: 0});
                        mp.get(key).count++;
                        return mp;
                    }, new Map).values()];

                    // console.log("grouped", grouped);
                    // console.log(result);
                    // result.map((ele, i) => (
                    //     <LogItem item={ele} key={i}/>
                    // ));
                    console.log("result0", result);
                }
            )
    };

    return (
        <React.Fragment>
            {
                clearDuplicates(cdrsData)
            }
            {
                _renderLogs(cdrsData)
            }
        </React.Fragment>
    );

};


export default GroupedLog;
