import React, {useEffect, useState} from "react";
import avatarImage from "../../images/avatar.jpg";
import {useDispatch, useSelector} from "react-redux";
import RosterStatusDetector from "../roster-status-detector";
import $ from "jquery";
import "./style.css";

const ContactItem = (props) => {
    const dispatch = useDispatch();
    const connection = useSelector(state => state.connection);
    const contacts = useSelector(state => state.contacts);
    const $iq = useSelector(state => state.$iq);
    const showMainPage = useSelector(state => state.showMainPage);
    const [lastActivity, setLastActivity] = useState({})
    const handleRosterClick = (contact) => {
        console.log(contact);
        dispatch({contact, type: "SET_SELECTED_ROSTER"});
    };
    const getLastActivity = () => {
        let lastIq = $iq({
            type: "get",
            from: connection.jid,
            to: props.item.jid,
            id: "last1"
        }).c("query", {xmlns: "jabber:iq:last"});
        connection.send(lastIq, onLastActivity());
    };
    useEffect(() => {
        getLastActivity();
        onLastActivity()
    },[contacts]);
    const onLastActivity = (iq) => {
        let from = $(iq).attr("from"); // the jabber_id of the contact\+
        let lastActivityObj;
        console.log("lastActivity", lastActivity)

        $(iq).each(function () {
            console.log("iq", $(iq))

            if ($(iq).attr('type') === "result") {
                console.log("result", $(iq))
                let lastActivityTime = $(iq).find("query").attr('seconds');
                lastActivityObj = {
                    from: from,
                    time: lastActivityTime
                };

                console.log("lastActivityObj", lastActivityObj);

                setLastActivity(lastActivityObj);


            }
            console.log("setLastActivity", lastActivity);

        });
    };

    return (
        <React.Fragment>
            {/*<button onClick={getLastActivity}>last activity</button>*/}
            {/*<p>{lastActivity}</p>*/}
            <div
                onClick={() => handleRosterClick(props.item.jid)}
                className="row item"
                id={props.item.jid}
            >
                <div className="avatar">

                    <RosterStatusDetector/>
                    <img alt="avatar" src={avatarImage}/>
                </div>
                <div className="right-side">
                    <div className="extension">
                        <p>112</p>
                    </div>

                    <div className="contact-name">
                        <p>{props.item.name}</p>
                    </div>
                </div>
            </div>

        </React.Fragment>
    );

};


export default ContactItem;
