import React from "react";


class RadioButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            statuses: this.props.statuses
        };
    }
    onStatusChanged (e) {
        this.setState({
            site: e.target.value
        });
    }

    render() {
        return (
            <React.Fragment>

                {this.state.statuses.map((item, i) => {
                    return (
                        <div className="radio">
                            <label>
                                <input type="radio" value={item} onChange={this.onStatusChanged}/>
                                {item}
                            </label>
                        </div>
                    );
                })}

            </React.Fragment>
        );
    }
}

export default RadioButton;
