import React from "react";
import FormControl from '@material-ui/core/FormControl';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import StatusDetector from "../status-detector";

class RadioButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            connection: this.props.connection,
            pres: this.props.pres,
            status: "Online",
            showDetector: false
        };
        this.setStatus = this.setStatus.bind(this);
        this.handleChange = this.handleChange.bind(this);

    }

    setStatus(s) {
        console.log("setStatus: " + s);
        let status = this.state.pres().c("show").t(s);
        this.state.connection.send(status);
    }

    handleChange(event) {
        this.setState({
            status: event.target.value
        })
        this.setStatus(event.target.value)
    }

    render() {
        return (
            <React.Fragment>


                {this.state.showDetector ? <StatusDetector myStatus={this.state.status}/> :
                    <FormControl component="fieldset">
                        <RadioGroup aria-label="My Status" name="My Status" value={this.state.status}
                                    onChange={this.handleChange}>
                            <FormControlLabel value="online" control={<Radio/>} label="Online" />
                            <FormControlLabel value="away" control={<Radio/>} label="Away"/>
                            <FormControlLabel value="dnd" control={<Radio/>} label="Do not Disturb"/>
                            <FormControlLabel value="invisible" control={<Radio/>} label="Invisible"/>

                        </RadioGroup>
                    </FormControl>}
            </React.Fragment>
        );
    }
}

export default RadioButton;
