class ConfigurationError extends Error {
  constructor(parameter, value) {
    super();
    this.code = 1;
    this.name = 'CONFIGURATION_ERROR';
    this.parameter = parameter;
    this.value = value;
    this.message = !this.value ? "Missing parameter: ".concat(this.parameter) : "Invalid value ".concat(JSON.stringify(this.value), " for parameter \"").concat(this.parameter, "\"");
  }

}

class InvalidStateError extends Error {
  constructor(status) {
    super();
    this.code = 2;
    this.name = 'INVALID_STATE_ERROR';
    this.status = status;
    this.message = "Invalid status: ".concat(status);
  }

}

class NotSupportedError extends Error {
  constructor(message) {
    super();
    this.code = 3;
    this.name = 'NOT_SUPPORTED_ERROR';
    this.message = message;
  }

}

class NotReadyError extends Error {
  constructor(message) {
    super();
    this.code = 4;
    this.name = 'NOT_READY_ERROR';
    this.message = message;
  }

}

module.exports = {
  ConfigurationError,
  InvalidStateError,
  NotSupportedError,
  NotReadyError
};
