import React from "react";

const ChatInput = (props) => {

    return (
        <React.Fragment>
            <input
                type="text"
                id="msg-here"
                className="form-control"
                placeholder="Message here..."
                rows="4"
                value={props.messageText.txt}
                onChange={props.onInputChange}

            />

            <button
                type="button"
                value="send"
                className="primary"
                id="sendButton"
                onClick={props.sendText}
            >send
            </button>
        </React.Fragment>
    )
};

export default ChatInput;
