import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import ButtonGroup from 'bootstrap';
import Button from 'bootstrap';
import Input from 'bootstrap';


const RadioButtonList = () => {
    const status = useSelector(state => state.status)
    const $pres = useSelector(state => state.$pres)
    const connection = useSelector(state => state.connection)
    const dispatch = useDispatch();

    const setStatus = (s) => {
        console.log("setStatus: " + s);
        connection.send($pres().c("show").t(s));
        dispatch({s, type: 'MY_STATUS'})
    }

    const handleChange = (event) => {
        setStatus(event)

    }

    return (
        <React.Fragment>
            <Grid>
                <Col sm={12} style={{marginBottom: '1em'}}>
                    <h1>&lt;RadioGroup/&gt;</h1>
                    <RadioGroup
                        name="test"
                        onChange={this.handleChange}
                        options={[
                            ['dairy', 'Cheese'],
                            ['fruit', 'Apple'],
                            ['meat', 'Ham'],
                        ]}
                        value={this.state.test}
                    />
                </Col>

            </Grid>


        </React.Fragment>
    );
}

export default RadioButtonList;
