import React from "react";
import $ from "jquery";

class StatusDetector extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            myStatus: this.props.myStatus
        };
    }

    componentDidUpdate(prevProps, prevState) {

        if (prevProps.myStatus !== this.props.myStatus) {
            this.setState({
                myStatus: this.props.myStatus
            });
        }
    }

    render() {
        return (
            <React.Fragment>

                <span
                    className={this.state.myStatus === "online" ? "online" :
                        "dnd"}>status</span>
            </React.Fragment>
        );
    }
}

export default StatusDetector;
