import React, {useEffect, useState} from "react";
import avatarImage from "../../images/avatar.jpg";
import "./style.css";
import {useDispatch, useSelector} from "react-redux";
import $ from "jquery";

const ContactItem = (props) => {
        const dispatch = useDispatch();


        const handleRosterClick = (contact) => {
            console.log(contact)
            dispatch({contact, type: "SET_SELECTED_ROSTER"});
        };

        return (
            <React.Fragment>
                <div
                    onClick={() => handleRosterClick(props.item.jid)}
                    className="row item"
                    id={props.item.jid}
                >
                    <div className="avatar">

                    <span data-id={props.item.jid}
                          className={props.rosterStatusColor === "xa" ? "offline" : props.rosterStatusColor}></span>
                        <img alt="avatar" src={avatarImage}/>
                    </div>
                    <div className="right-side">
                        <div className="extension">
                            <p>112</p>
                        </div>

                        <div className="contact-name">
                            <p>{props.item.name}</p>
                        </div>
                    </div>
                </div>

            </React.Fragment>
        );


    }
;


export default ContactItem;
