const initialState = {
    status: '',
    connection: new window.Strophe.Connection(
        "http://77.104.116.225:5280/http-bind"
    ),
    $pres: window.$pres,
    $iq: window.$iq,
    strophe: window.Strophe,
    showMainPage: false
};
const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'MY_STATUS': {
            return {
                ...state,
                status: action.s
            };
        }
        case 'SHOW_MAIN_PAGE': {
            return {
                ...state,
                showMainPage: action.showMainPage
            };
        }

        default:
            return state;
    }
};
export default reducer;
