import React, {useEffect, useState} from "react";
import avatarImage from "../../images/avatar.jpg";
import "./style.css";
import {useSelector} from "react-redux";

const ContactItem = (props) => {
    const contacts = useSelector(state => state.contacts);
    const rosterStatus = useSelector(state => state.rosterStatus);
    const selectedRoster = useSelector(state => state.selectedContact);
    const [rosterStatusColor, setRosterStatusColor] = useState(rosterStatus);
    const [selectedContact, setSelectedContact] = useState('')

    useEffect(() => {
        setRosterStatusColor(rosterStatus)
    }, [rosterStatus])


    return (
        <React.Fragment>
            <div
                onClick={() => setSelectedContact(props.item.jid)}
                className="row item"
                id={props.item.jid}
            >
                <div className="avatar">
                                <span
                                    className={rosterStatusColor === "xa" ? "offline" : rosterStatusColor}>tt</span>
                    <img alt="avatar" src={avatarImage}/>
                </div>
                <div className="right-side">
                    <div className="extension">
                        <p>112</p>
                    </div>

                    <div className="contact-name">
                        <p>{props.item.name}</p>
                    </div>
                </div>
            </div>

        </React.Fragment>

    );
}


export default ContactItem;
