import React from "react";
import "./style.css";

const MessagePopUp = (props) => {
    return (
        <React.Fragment>
            <div className={props.item.type === "rec" ? "rec-texts" : "sent-texts"}>
                <div className={props.item.type === "rec" ? "rec" : "sent"}>
                    <p>
                        {props.item.type === "rec" ? null : <strong>me : </strong>}
                        {props.item.message}
                    </p>
                </div>
            </div>
        </React.Fragment>
    );
}


export default MessagePopUp;
