const initialState = {
    status: "chat",
    connection: new window.Strophe.Connection(
        "http://77.104.116.225:5280/http-bind"
    ),
    $pres: window.$pres,
    $iq: window.$iq,
    $msg: window.$msg,
    strophe: window.Strophe,
    showMainPage: false,
    showCallPage: false,
    contacts: [],
    rosterStatus: [],
    selectedContact: '',
    rosterClicked: false,
    sendLastActivity: false,
    devicesData: [],
    loginData: [],
    callFlowsData: [],
    cdrsData: [],
    loadingStatus: true,
    newMessage: '',
    historyMessages: []
};
const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'MY_STATUS': {
            return {
                ...state,
                status: action.s
            };
        }
        case 'SHOW_MAIN_PAGE': {
            return {
                ...state,
                showMainPage: action.showMainPage
            };
        }
        case 'SHOW_CALL_PAGE': {

            return {
                ...state,
                showCallPage: action.showPage
            };
        }
        case 'GET_CONTACTS': {
            return {
                ...state,
                contacts: action.contacts
            };
        }
        case 'CHANGE_EACH_ROSTER_STATUS': {

            return {
                ...state,
                rosterStatus: state.rosterStatus.map(
                    (content, i) => content.id === action.rosterStatusItem.id ? {
                            ...content,
                            status: action.rosterStatusItem.status
                        }
                        : content
                )
            }

        }
        case 'PUSH_ROSTER_STATUS_ITEM': {
            let rostersArray = state.rosterStatus;
            rostersArray.push(action.rosterStatusItem)
            return {
                ...state,
                rosterStatus: rostersArray
            }
        }
        case 'SET_SELECTED_ROSTER': {
            return {
                ...state,
                selectedContact: action.contact
            };
        }
        case 'CHECK_ROSTER_CLICK': {

            return {
                ...state,
                rosterClicked: action.rosterClicked
            };
        }
        case 'GET_DEVICES_DATA': {
            return {
                ...state,
                devicesData: action.devicesData
            }
        }
        case 'GET_LOGIN_DATA': {
            return {
                ...state,
                loginData: action.loginData
            }
        }
        case 'GET_USER_CALLFLOWS_DATA': {
            return {
                ...state,
                callFlowsData: action.callFlowsData
            }
        }
        case 'GET_CDRS_DATA': {
            return {
                ...state,
                cdrsData: action.cdrsData
            }
        }
        case 'SET_LOADING_STATUS': {
            return {
                ...state,
                loadingStatus: action.loadingStatus
            }
        }
        case 'GET_NEW_MESSAGE':{
            return {
                ...state,
                newMessage: action.newMessage
            }
        }
        case 'FETCH_HISTORY_MESSAGES':{
            return {
                ...state,
                historyMessages: action.historyMessages
            }

        }
        case 'PUSH_HISTORY_MESSAGES': {
            debugger;
            let historyArray = state.historyMessages;
            historyArray.push(action.newMessage)
            return {
                ...state,
                historyMessages: historyArray
            }
        }
        default:
            return state;
    }
};
export default reducer;
