import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import axios from "axios";
import {getCookie} from "../../helper/functions";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faArrowLeft, faArrowRight, faBan} from "@fortawesome/free-solid-svg-icons";


const CallLogs = () => {
    const cdrsData = useSelector(state => state.cdrsData);
    const moment = require('persian-date');
    const orgMoment = require('moment');
    const jalali = require('moment-jalaali');
    const [cdrData, getCdrData] = useState({item: []});
    const dispatch = useDispatch();
    let today = orgMoment();
    console.log('today', today.startOf('day').unix());
    const todayCalls = () => {
        axios.get("http://77.104.116.187:8000/v2/accounts/" + getCookie("accID") + "/users/" + getCookie("owID") + "/cdrs", {
            headers: {
                "X-Auth-Token": getCookie("monster_token")
            }
        })
            .then(response => {
                let cdrs = cdrData.item;
                cdrs.push(response.data)
                getCdrData({...cdrData, item: cdrs});
                setCdrsDataToReducer(cdrData.item[0].data);
                console.log("response.cdrs", cdrData.item[0].data);
            })
            .catch(error => {

            });
    };
    // daily api query
    // interaction?created_from=" + today.startOf('day').unix()
    //     + "&created_to=" + today.endOf('day').unix()
    //     + "&page_size=10" + "&_=" + new Date().setUTCSeconds(0)


    /*------set CDRs per user data to reducer-----------------*/
    const setCdrsDataToReducer = (cdrsData) => {
        dispatch({cdrsData, type: 'GET_CDRS_DATA'})
    };
    /*------set CDRs per user data to reducer-----------------*/
    return (
        <React.Fragment>
            <div className="filter-by-time">
                <button className="btn-sm badge-light " onClick={todayCalls}>Today</button>
                <button className="btn-sm badge-light">This Week</button>
                <button className="btn-sm badge-light">This Month</button>
            </div>
            <div className="filter-by-type">
                <button className="btn-sm badge-light ">Incoming Calls</button>
                <button className="btn-sm badge-light">Missed Calls</button>
                <button className="btn-sm badge-light">Dialed Calls</button>
            </div>
            <table className="table table-sm table-striped table-bordered table-hover">
                <thead>
                <tr>
                    <th>
                        <FontAwesomeIcon color="green" icon={faArrowLeft}/><br/>
                        <FontAwesomeIcon color="orange" icon={faArrowRight}/>
                    </th>
                    <th>Date/Time</th>
                    <th>From</th>
                    <th>To</th>
                    <th>Duration</th>
                </tr>
                </thead>
                <tbody>{

                    cdrsData === undefined ? null : cdrsData.map((item, i) => {

                        return (
                            <tr key={i}>
                                <td>
                                    {
                                        item.direction === "inbound" ?
                                            <FontAwesomeIcon color="green"
                                                             icon={faArrowLeft}/> : item.direction === "outbound" ?
                                            <FontAwesomeIcon color="orange" icon={faArrowRight}/> :
                                            <FontAwesomeIcon color="red" icon={faBan}/>

                                    }
                                </td>
                                <td>

                                    {
                                        jalali.unix(item.unix_timestamp).format('jYYYY/jM/jD, HH:mm:ss')
                                    }

                                </td>
                                <td>
                                    {item.from}
                                </td>
                                <td>
                                    {item.to}
                                </td>
                                <td>
                                    {item.duration_seconds}
                                </td>
                            </tr>
                        )

                    })
                }
                </tbody>
            </table>
        </React.Fragment>
    )

};


export default CallLogs;
