import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import ButtonGroup from 'bootstrap';
import Button from 'bootstrap';


const RadioButtonList = () => {
    const status = useSelector(state => state.status)
    const $pres = useSelector(state => state.$pres)
    const connection = useSelector(state => state.connection)
    const dispatch = useDispatch();

    const setStatus = (s) => {
        console.log("setStatus: " + s);
        connection.send($pres().c("show").t(s));
        dispatch({s, type: 'MY_STATUS'})
    }

    const handleChange = (event) => {
        setStatus(event)

    }

    return (
        <React.Fragment>
            <ButtonGroup>
                <Button active>Radio 1
                    <Input ref="input1" type="radio" name="radioButtonSet" value='input1' standalone defaultChecked/>
                </Button>
                <Button>Radio 2
                    <Input ref="input2" type="radio" name="radioButtonSet" value='input2' standalone/>
                </Button>
            </ButtonGroup>


        </React.Fragment>
    );
}

export default RadioButtonList;
