import React, {useEffect, useState} from "react";
import "./style.css"
import Device from "../Device";
import Extension from "../extension";
import CallLogs from "../call-log";
import {useDispatch, useSelector} from "react-redux";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSpinner} from "@fortawesome/free-solid-svg-icons";

const CallInformation = () => {
    const loading = useSelector(state => state.loadingStatus);
    const [loadingStatus, setLoadingStatus] = useState(true);
    const callFlowsData = useSelector(state => state.callFlowsData);

    const dispatch = useDispatch();
    useEffect(() => {
        setLoadingStatus(false);
        dispatch({loadingStatus, type: "SET_LOADING_STATUS"});
    }, [callFlowsData]);
    return (

        <React.Fragment>
            <div className="call-spinner"><FontAwesomeIcon className="fas fa-spinner fa-spin" icon={faSpinner}/></div>
            {/*{*/}
            {/*    loading ? <div className="call-spinner"><FontAwesomeIcon className="fas fa-spinner fa-spin" icon={faSpinner}/></div> :*/}
            {/*        <div className="container">*/}
            {/*            <Device/>*/}
            {/*            <Extension/>*/}
            {/*            <CallLogs/>*/}
            {/*        </div>*/}
            {/*}*/}
        </React.Fragment>
    )
};


export default CallInformation;
