import React, {useEffect, useState} from "react";
import MessagePopUp from "../message-popup";
import "./style.css";
import $ from "jquery";
import {useSelector} from "react-redux";

const ChatBox = () => {
    const selectedContact = useSelector(state => state.selectedContact);
    const $iq = useSelector(state => state.$iq);
    const $msg = useSelector(state => state.$msg);
    const connection = useSelector(state => state.connection);

    const [historyMessages, setHistoryMessages] = useState([]);
    const [allMessages, setAllMessages] = useState([]);
    const [messageText, changeMessageText] = useState("");

    useEffect(() => {
        getLog();
    }, [selectedContact]);

    const getLog = () => {
        console.log("connection.mam", connection.mam);
        let sentMessages,
            recMessages,
            history = [],
            archiveIq = $iq({
                type: "set",
                id: "archive1"

            }).c("query", {
                xmlns: "urn:xmpp:mam:2"
            });
        connection.mam.query(connection.send(archiveIq), {
            with: selectedContact,
            onMessage: function (message) {
                $(message).each(function () {
                    if (
                        $(this)
                            .find("forwarded message")
                            .attr("from")
                            .split("/", 1)[0] === selectedContact
                    ) {
                        recMessages = {
                            text: $(this)
                                .find("forwarded message body")
                                .text(),
                            stamp: $(this)
                                .find("delay")
                                .attr("stamp"),
                            type: "rec"
                        };
                        history = history.concat(recMessages);
                    } else if (
                        $(this)
                            .find("forwarded message")
                            .attr("from")
                            .split("/", 1)[0] !== selectedContact
                    ) {
                        sentMessages = {
                            text: $(this)
                                .find("forwarded message body")
                                .text(),
                            stamp: $(this)
                                .find("delay")
                                .attr("stamp"),
                            type: "sent"
                        };

                        history = history.concat(sentMessages);
                    }
                });

                return true;
            },
            onComplete: function (response) {
                console.log("historyMessages", historyMessages)
                setHistoryMessages(history);
                let chatDiv = document.querySelector(".all-messages");
                chatDiv.scrollTo(0, chatDiv.scrollHeight);
            }.bind(this)
        });
    }


    const handleChangeMsg = (event) => {
        changeMessageText(event.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        let txtJID = selectedContact,
            txtMsg = messageText,
            currentMessages = [],
            message = {
                to: txtJID,
                message: txtMsg,
                time: new Date(),
                type: "sent"
            };

        console.log("txtJID", txtJID)
        console.log("txtMsg", txtMsg)
        currentMessages = currentMessages.concat(message)
        if (txtMsg === undefined || txtMsg === "")
            alert("Empty Message, please type something");
        else {
            setAllMessages(allMessages.push(currentMessages));
            sendMessage(message);
            console.log("message", messageText)
            console.log("currentMessages", currentMessages)
            console.log("allmessages", allMessages[0])
        }
        connection.addHandler(
            onMessage,
            null,
            "message",
            null,
            null,
            null
        );
    };

    //Sending message to contact
    const sendMessage = (msg) => {
        let reply = $msg({
            to: msg.to,
            from: connection.jid,
            type: "chat",
            id: "event1"
        })
            .c("body")
            .t(msg.message)
            .up()
            .c("request", {xmlns: "urn:xmpp:receipts"});

        connection.send(reply.tree());
    }

    const onMessage = (msg) => {
        let from = msg.getAttribute("from");
        let type = msg.getAttribute("type");
        let elems = msg.getElementsByTagName("body");

        if (type === "error") {
            alert("An error occured! ");
            return;
        }

        if (type === "chat") {
            let message = {
                from: from,
                message: elems[0].innerHTML,
                time: new Date(),
                type: "rec"
            };
            setAllMessages(message)
        }

        return true; //The handler should return true if it is to be invoked again; returning false will remove the handler after it returns
    }


    return (
        <React.Fragment>
            <p className="userId">To : {selectedContact}</p>
            <div className="all-messages">
                <div className="archive">
                    {" "}
                    {historyMessages.map((item, i) => {
                        return (
                            <MessagePopUp
                                key={i}
                                sentMsg={item.text}
                                type={item.type}
                            />
                        );
                    })}
                </div>
                <div className="messages">
                    {allMessages[0].map((item, i) => {
                        return (
                            <MessagePopUp
                                key={i}
                                type={item.type}
                                sentMsg={item.message}
                            />
                        );
                    })}

                </div>
            </div>

            <div className="textbox">
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        id="msg-here"
                        className="form-control"
                        placeholder="Message here..."
                        rows="4"
                        onChange={handleChangeMsg}
                    />

                    <button
                        type="button"
                        value="send"
                        className="primary"
                        id="sendButton"
                    >send
                    </button>
                </form>
            </div>
        </React.Fragment>
    );
}


export default ChatBox;
