import React, {useEffect, useState} from "react";
import avatarImage from "../../images/avatar.jpg";
import {useDispatch, useSelector} from "react-redux";
import RosterStatusDetector from "../roster-status-detector";
import $ from "jquery";
import "./style.css";

const ContactItem = (props) => {
    const dispatch = useDispatch();
    const [lastActivity, setLastActivity] = useState({from: '', time: ''});
    const [userStatus, setUserStatus] = useState({})
    const rosterStatus = useSelector(state => state.rosterStatus);

    const handleRosterClick = (contact) => {
        console.log(contact);
        dispatch({contact, type: "SET_SELECTED_ROSTER"});
    };

    /*----------find status for specific user------------------*/
    const findRosterStatus = () => {
        const item = rosterStatus.filter(i => i.id === props.item.jid)[0]
        setUserStatus(item);
    };
    /*--------------/find status for specific user------------------*/



    useEffect(() => {
        findRosterStatus()
    }, [rosterStatus]);

    const millisToMinutesAndSeconds = (millis) => {
        let minutes = Math.floor(millis / 60000);
        let seconds = ((millis % 60000) / 1000).toFixed(0);

        return minutes;
    };
    return (
        <React.Fragment>
            {/*<button onClick={getLastActivity}>last activity</button>*/}
            {/*<p>{lastActivity === 0 ? "online" : millisToMinutesAndSeconds(lastActivity.time) <= 30*/}
            {/*    ? "last seen recently" : millisToMinutesAndSeconds(lastActivity.time) < 1440*/}
            {/*        ? "last seen within a day" : "last seen long time ago"}</p>*/}
            <p>{lastActivity.time}</p>
            <div
                onClick={() => handleRosterClick(props.item.jid)}
                className="row item"
                id={props.item.jid}
            >
                <div className="avatar">
                    {
                        userStatus !== undefined ? <RosterStatusDetector status={userStatus.status}/> :
                            <span className="offline"></span>
                    }
                    <img alt="avatar" src={avatarImage}/>
                </div>
                <div className="right-side">
                    <div className="extension">
                        <p>112</p>
                    </div>

                    <div className="contact-name">
                        <p>{props.item.name}</p>
                    </div>
                </div>
            </div>

        </React.Fragment>
    );

};


export default ContactItem;
