import React, {useState} from "react";
import FormControl from '@material-ui/core/FormControl';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import {useDispatch, useSelector} from "react-redux";

const RadioButton = () => {
    const status = useSelector(state => state.status)
    const $pres = useSelector(state => state.$pres)
    const connection = useSelector(state => state.connection)
    const dispatch = useDispatch();

    const setStatus = (s) => {
        console.log("setStatus: " + s);
        status(s);
        connection.send($pres().c("show").t(s));
    }

    const handleChange = (event) => {
        setStatus(event.target.value)
        // dispatch({status, type: 'MY_STATUS'})

    }

    return (
        <React.Fragment>


            <FormControl component="fieldset">
                <RadioGroup aria-label="My Status" name="My Status" value={status}
                            onChange={handleChange}>
                    <FormControlLabel value="online" control={<Radio/>} label="Online"/>
                    <FormControlLabel value="away" control={<Radio/>} label="Away"/>
                    <FormControlLabel value="dnd" control={<Radio/>} label="Do not Disturb"/>
                    <FormControlLabel value="invisible" control={<Radio/>} label="Invisible"/>

                </RadioGroup>
            </FormControl>
        </React.Fragment>
    );
}

export default RadioButton;
