import React from "react";
import axios from "axios";
import {getCookie} from "../../helper/functions";

const ChangePassword = () => {

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            handleSubmit(event);
        }

    };
    const handleSubmit = () => {

        axios.get("http://77.104.116.187:8000/v2/accounts/" + getCookie("accID") + "/callflows/?filter_owner_id=" + getCookie("owID"), {
            headers: {
                "X-Auth-Token": getCookie("monster_token")
            }
        })
            .then(response => {

            })
            .catch(error => {
                console.log(error);

            });
    };
    return (
        <React.Fragment>
            <form className="form-signin">
                <div className="form-label-group">
                    <input
                        className="form-control"
                        size="col-sm-10"
                        type="text"
                        id="inputNewPass"
                        placeholder="New password"
                        onChange={handleChangepass}
                        onKeyDown={handleKeyDown}
                        required autoFocus
                    />
                    <label htmlFor="inputNewPass">Password</label>
                    <input
                        className="form-control"
                        size="col-sm-10"
                        type="text"
                        id="inputConfirm"
                        placeholder="Confirm password"
                        onChange={handleConfirmpass}
                        onKeyDown={handleKeyDown}
                        required autoFocus
                    />
                    <label htmlFor="inputConfirm">Confirm Password</label>
                    <button
                        type="submit"
                        className="btn btn-lg btn-primary btn-block text-uppercase"
                        id="connect"
                        onClick={handleSubmit}
                    >Change Password</button>
                </div>
            </form>
        </React.Fragment>
    );

}


export default ChangePassword;
