import React from "react";
import "./style.css";
import Tab from "react-bootstrap/Tab";
import Tabs from "react-bootstrap/Tabs";
import ContactsList from "../contacts-list";
import ChatPanel from "../chat-panel";
import CallInformation from "../../call/call-information";
import {useSelector} from "react-redux";

const TabsSection = () => {
    const selectedContact = useSelector(state => state.selectedContact);

    return (
        <React.Fragment>
            <div className="tabs">
                <Tabs defaultActiveKey="Chat" disabled={true}>
                    <Tab eventKey="Chat" className="chat-tab" title="پیام ها"  >

                        <ContactsList/>
                        {
                            selectedContact !== ''  ? <ChatPanel/> : <span className="default-text">Select a contact to start messaging...</span>
                        }

                    </Tab>
                    <Tab eventKey="Call Log" title="تماس ها"  disabled={true}>
                        <CallInformation/>
                    </Tab>

                    <Tab eventKey="Missed Calls" title="مخاطبین" disabled={true}></Tab>
                </Tabs>
            </div>
        </React.Fragment>
    )
}


export default TabsSection;
