import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Button, ButtonGroup} from 'reactstrap';

const RadioButtonList = () => {
    const status = useSelector(state => state.status)
    const $pres = useSelector(state => state.$pres)
    const connection = useSelector(state => state.connection)
    const dispatch = useDispatch();

    const setStatus = (s) => {
        console.log("setStatus: " + s);
        connection.send($pres().c("show").t(s));
        dispatch({s, type: 'MY_STATUS'})
    }

    const handleChange = (event) => {
        setStatus(event)

    }

    return (
        <div>
            <ButtonGroup onChange={handleChange}>
                <Button color="primary" onClick={handleChange} active={status === "online"}>Online</Button>
                <Button color="primary" active={status === "away"}>Away</Button>
                <Button color="primary" active={status === "dnd"}>Do Not
                    Disturb</Button>
                <Button color="primary"
                        active={status === "invisible"}>Invisible</Button>
            </ButtonGroup>
        </div>
    );
}

export default RadioButtonList;
