import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import $ from "jquery";

const LoginPage = () => {
    const connection = useSelector(state => state.connection)
    const $pres = useSelector(state => state.$pres)
    const $iq = useSelector(state => state.$iq)
    const strophe = useSelector(state => state.strophe)
    const showMainPage = useSelector(state => state.showMainPage)
    const myJID = useSelector(state => state.myJID)
    const contacts = useSelector(state => state.contacts)
    const rosterStatus = useSelector(state => state.rosterStatus)

    const [jidValue, setJidValue] = useState('')
    const [passValue, setPassValue] = useState('')
    const [loginButton, changeLoginButton] = useState('connect')
    const [rosters, createRosters] = useState({})
    const dispatch = useDispatch();

    //strophe connection process
    const onConnect = (status) => {
        console.log("window.Strophe.Status", strophe.Status);
        if (status === strophe.Status.CONNECTING) {
            console.log("Strophe is connecting.");
        } else if (status === strophe.Status.CONNFAIL) {
            console.log("Strophe failed to connect.");
            setShowStatus(false)
        } else if (status === strophe.Status.AUTHENTICATING) {
            console.log("Strophe is AUTHENTICATING.");
        } else if (status === strophe.Status.AUTHFAIL) {
            console.log("Strophe is AUTHFAIL.");
            setShowStatus(false)
        } else if (status === strophe.Status.DISCONNECTING) {
            console.log("Strophe is disconnecting.");
        } else if (status === strophe.Status.DISCONNECTED) {
            console.log("Strophe is disconnected.");
            setShowStatus(false)
        } else if (status === strophe.Status.CONNECTED) {
            console.log("Strophe is connected.");
            setShowStatus(true)
            getRoster();
            sendPriority();
            connection.send($pres());

            console.log("jid", connection.jid)
        }
    }
    /*-----------------Get Contacts List (step1)----------------=*/
    //send xml to get contacts from xmpp server
    const getRoster = () => {
        const newIq = $iq({type: "get", id: "r1"})
            .c("query", {xmlns: "jabber:iq:roster"})
        connection.sendIQ(newIq, rosterReceived);
        // connection.sendIQ(newIq);
    }

    /*-----------------Get Contacts List (step1)----------------*/

    /*-----------------Received contacts Time (step2)----------------*/
    //how to use received rosters from xmpp server
    const rosterReceived = (iq) => {
        let contactJid,
            rosterName,
            myContacts = [];
        $(iq)
            .find("item")
            .each(function () {
                contactJid = $(this).attr("jid"); // The jabber_id of your contact
                if ($(this).attr("name")) {
                    rosterName = $(this).attr("name");
                } else {
                    rosterName = "";
                }
                // The jabber_id of your contact
                // You can probably put them in a unordered list and and use their jids as ids.
                myContacts = myContacts.concat({
                    name: rosterName,
                    jid: contactJid
                });
            });
        getContacts(myContacts)
        connection.addHandler(
            onPresence,
            null,
            "presence",
            null,
            null,
            null
        );
    }

    /*-----------------/Received contacts (step2)----------------*/
    /*----------get contact presence-----------*/
    //use presences from xmpp server
    const onPresence = (presence) => {
        console.log("onPresence");

        let presence_type = $(presence).attr("type"); // unavailable, subscribed, etc...
        let from = $(presence).attr("from"); // the jabber_id of the contact

        if (!presence_type) {
            changeRosterStatus("chat")

        };
        if (presence_type !== "error") {
            if (presence_type === "unavailable") {
                // Mark contact as offline
                // changeRosterStatus("offline")

                console.log(from + "contact as unavailable");

            } else {
                let show = $(presence)
                    .find("show")
                    .text(); // this is what gives away, dnd, etc.
                if (show === "chat") {
                    // changeRosterStatus("chat")
                    // Mark contact as online
                    console.log(from + "contact as online");

                } else if (show === "away") {
                    // etc...
                    // changeRosterStatus("away")
                    console.log(from + "contact as away");

                } else if (show === "dnd") {
                    // etc...
                    // changeRosterStatus("dnd")
                    console.log(from + "contact as dnd");

                } else if (show === "xa") {
                    // etc...
                    // changeRosterStatus("xa")
                    console.log(from + "contact as xa");

                }
            }
        }


        return true; //The handler should return true if it is to be invoked again; returning false will remove the handler after it returns
    }

    /*----------get contact presence-----------*/
    /*-----------------Set priority ----------------*/
    //set priority to specify the device
    const sendPriority = () => {
        connection.send(
            $pres()
                .c("priority")
                .t("10")
                .up()
                .c("rap", {xmlns: "urn:xmpp:rap:0", ns: "urn:xmpp:jingle:apps:rtp:0", num: '5'})
        );
        //send request for available features
        connection.sendIQ($iq({type: "get"})
            .c("query", {xmlns: "http://jabber.org/protocol/disco#info"}))
    }

    /*-----------------/Get priority ----------------*/
    /*---------------------------------*/
    //Show Or hide Main page.
    const setShowStatus = (showMainPage) => {
        console.log("show", showMainPage)
        dispatch({showMainPage, type: 'SHOW_MAIN_PAGE'})
    }
    /*---------------------------------*/
    /*---------------------------------*/
    //change each roster status.
    const changeRosterStatus = (rosterStatus) => {
        console.log("rosterStatus", rosterStatus)
        dispatch({rosterStatus, type: 'CHANGE_EACH_ROSTER_STATUS'})
    }
    /*---------------------------------*/

    /*---------------------------------*/
    //set received contacts from xmpp server to global state
    const getContacts = (contacts) => {
        console.log("GET_CONTACTS", contacts)
        dispatch({contacts, type: 'GET_CONTACTS'})
    }
    /*---------------------------------*/

    /*-----------------------------------*/
    //action for login form
    const handleChangeJID = (event) => {
        // console.log("jidValue", event.target.value)
        setJidValue(event.target.value)
        console.log("jidValue", jidValue)

    }
    const handleChangePass = (event) => {
        // console.log("passValue", event.target.value)
        setPassValue(event.target.value)
        console.log("passValue", ...passValue)

    }
    const handleSubmit = (event) => {
        event.preventDefault();
        if (jidValue === undefined || jidValue === "") {
            alert("Blank JID");
        } else if (passValue === undefined || passValue === "")
            alert("Empty pass, please type password");
        else {
            if (loginButton === "connect") {
                changeLoginButton("disconnect")
                connection.connect(
                    jidValue,
                    passValue,
                    onConnect
                );
            } else {
                connection.disconnect();
                changeLoginButton("connect")

            }
        }
    }
    /*-----------------------------------*/

    return (


        <React.Fragment>

            <div className="row">
                <div className="col">
                    <form>
                        <input
                            label="JID"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="text"
                            placeholder="enter your JID..."
                            onChange={handleChangeJID}
                            value={jidValue}
                        />
                        <input
                            label="Password"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="password"
                            placeholder="enter your password..."
                            onChange={handleChangePass}
                            value={passValue}
                        />

                        <button
                            type="button"
                            className="btn btn-primary"
                            id="connect"
                            value={loginButton}
                            onClick={handleSubmit}
                        >{loginButton}</button>
                    </form>
                </div>
            </div>
        </React.Fragment>
    );
}


export default LoginPage;
