import React, {useState} from "react";
import axios from 'axios';
import {useDispatch, useSelector} from "react-redux";


const KazooLogin = () => {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [loginButton, changeLoginButton] = useState('connect');
    const showCallPage = useSelector(state => state.showCallPage);
    const [accId, getAccId] = useState('')
    const [authToken, setAuthToken] = useState('')
    const dispatch = useDispatch();
    const onKazooConnect = () => {

        axios.put('http://docker.nexfon.ir:8000/v2/user_auth', {
            data: {
                "account_name": userName.split("/", 1)[0],
                "credentials": "d2abaa37a7c3db1137d385e1d8c15fd2"
            }
        })
            .then(response => {
                console.log(response.data);
                setShowCallApp(true);
                let account_id = response.data.data.account_id;
                let auth_token = response.data.auth_token;
                getAccId(account_id);
                setAuthToken(auth_token);
                getDevicesList();
                console.log("accId", accId);
                console.log("auth_token", auth_token);
                document.cookie = "monster_token=" + auth_token
                document.cookie = "accID=" + account_id;

            })
            .catch(error => {
                console.log(error);
                setShowCallApp(false);
            });
    };
    const getDevicesList = () => {

        axios.get("http://docker.nexfon.ir:8000/v2/accounts/" + getCookie(accID) + "/devices/", {
            headers: {
                "X-Auth-Token": getCookie(monster_token)
            }
        })
            .then(response => {
                console.log(response.data);
            })
            .catch(error => {
                console.log(error);

            });
    }
    const getCookie = (cname) => {
        let name = cname + "=";
        let decodedCookie = decodeURIComponent(document.cookie);
        let ca = decodedCookie.split(';');
        for (let i = 0; i < ca.length; i++) {
            let c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
    /*---------------------------------*/
    //Show Or hide Main page.
    const setShowCallApp = (showPage) => {
        console.log("showPage", showPage)

        dispatch({showPage, type: 'SHOW_CALL_PAGE'})
    };
    /*---------------------------------*/
    //action for login form
    const handleChangeJID = (event) => {
        setUserName(event.target.value)
    };
    const handleChangePass = (event) => {
        // console.log("passValue", event.target.value)
        setPassword(event.target.value)
    };
    const handleSubmit = (event) => {

        event.preventDefault();
        if (userName === undefined || userName === "") {
            alert("Blank userName");
        } else if (password === undefined || password === "")
            alert("Empty pass, please type password");
        else {

            if (loginButton === "connect") {
                changeLoginButton("disconnect")
                onKazooConnect();
            } else {
                changeLoginButton("connect")

            }
        }
    };
    /*-----------------------------------*/

    return (
        <React.Fragment>

            <div className="row">
                <div className="col">
                    <form>
                        <input
                            label="JID"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="text"
                            placeholder="enter your JID..."
                            onChange={handleChangeJID}
                            value={userName}
                        />
                        <input
                            label="Password"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="password"
                            placeholder="enter your password..."
                            onChange={handleChangePass}
                            value={password}
                        />

                        <button
                            type="button"
                            className="btn btn-primary"
                            id="connect"
                            value={loginButton}
                            onClick={handleSubmit}
                        >{loginButton}</button>
                    </form>
                </div>
            </div>
        </React.Fragment>
    )
}


export default KazooLogin;
