            if (loginButton === "connect") {
                changeLoginButton("disconnect")
                connection.connect(
                    jidValue,
                    passValue,
                    onConnect
                );
            } else {
                connection.disconnect();
                changeLoginButton("connect")

            }
        }
    };
    /*-----------------------------------*/

    return (
        <React.Fragment>

            <div className="row">
                <div className="col">
                    <form>
                        <input
                            label="JID"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="text"
                            placeholder="enter your JID..."
                            onChange={handleChangeJID}
                            value={jidValue}
                        />
                        <input
                            label="Password"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="password"
                            placeholder="enter your password..."
                            onChange={handleChangePass}
                            value={passValue}
                        />

                        <button
                            type="button"
                            className="btn btn-primary"
                            id="connect"
                            value={loginButton}
                            onClick={handleSubmit}
                        >{loginButton}</button>
                    </form>
                </div>
            </div>
        </React.Fragment>
    )
}


export default KazooLogin;
