import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Button, ButtonGroup} from 'reactstrap';

const RadioButtonList = () => {
    const status = useSelector(state => state.status)
    const $pres = useSelector(state => state.$pres)
    const connection = useSelector(state => state.connection)
    const dispatch = useDispatch();

    const setStatus = (s) => {
        console.log("setStatus: " + s);
        connection.send($pres().c("show").t(s));
        dispatch({s, type: 'MY_STATUS'})
    }

    const handleChange = (event) => {
        setStatus(event)

    }

    return (
        <div>
            <h5>Radio Buttons</h5>
            <ButtonGroup>
                <Button color="primary" onClick={() => setStatus(1)} active={status === "online"}>Online</Button>
                <Button color="primary" onClick={() => setStatus(2)} active={status === "away"}>Away</Button>
                <Button color="primary" onClick={() => setStatus(3)} active={status === "dnd"}>Do Not Disturb</Button>
                <Button color="primary" onClick={() => setStatus(3)} active={status === 3}>Do Not Disturb</Button>
            </ButtonGroup>
            <p>Selected: {status}</p>
        </div>
    );
}

export default RadioButtonList;
